import {useEffect, useState} from "react";

function FetchRealTimeDataForCloseChart({ stockCode, onSDFCFetch }){
    useEffect(() => {
        const fetchData = () => {
            fetch(`http://127.0.0.1:8000/api/post_stock_real_data_for_code/${stockCode}/`)
                .then(response => {
                    if (!response.ok) {
                        throw new Error('Network response was not ok');
                    }
                    return response.json();
                })
                .then(data => {
                    setStockData(data)
                    if (onSDFCFetch) {
                        onSDFCFetch(data)
                        console.log(data)
                    }
                })
                .catch(error => console.error('Error fetching RSI data:', error))
        };

        // 페이지 로드 시 데이터 최초 1회 호출
        fetchData();

        // 주기적으로 데이터를 갱신 (5초마다 호출)
        const intervalId = setInterval(fetchData, 5000);

        // 컴포넌트 언마운트 시 인터벌 클리어
        return () => clearInterval(intervalId);
    }, [stockCode, stockData]);
    return null;
}

export default FetchRealTimeDataForCloseChart
