import React, { useState, useEffect } from "react";

function RSIAnalysis3({ rsiData }) {
    const [crossStatus, setCrossStatus] = useState(null);

    useEffect(() => {
        if (rsiData && rsiData.length > 1) {
            const currentRSI = rsiData[rsiData.length - 1].RSI.toFixed(2);
            const previousRSI = rsiData[rsiData.length - 2].RSI.toFixed(2);
            const crossed50Up = previousRSI < 50 && currentRSI >= 50;
            const crossed50Down = previousRSI > 50 && currentRSI <= 50;

            setCrossStatus({
                crossed50Up,
                crossed50Down,
                currentRSI
            });
        }
    }, [rsiData]);

    return (
        <div>
            <h5>RSI 중앙선(50선) 상향/하향 돌파 분석</h5>
            {crossStatus && (
                <>
                    {crossStatus.crossed50Up && (
                        <p style={{ color: 'green' }}>
                            RSI가 50선을 상향 돌파했습니다. 현재 RSI: {crossStatus.currentRSI}
                        </p>
                    )}
                    {crossStatus.crossed50Down && (
                        <p style={{ color: 'red' }}>
                            RSI가 50선을 하향 돌파했습니다. <br></br>
                            시장의 모멘텀이 약세로 전환되고 있음을 의미할 수 있습니다.<br></br>
                            현재 RSI: {crossStatus.currentRSI}으로 매도 타이밍으로 해석될 수 있으나 전체적인 분석 결과를 참조해주세요
                        </p>
                    )}
                    {!crossStatus.crossed50Up && !crossStatus.crossed50Down && (
                        <p>
                            RSI가 50선에서 큰 변동이 없습니다. 현재 RSI: {crossStatus.currentRSI}
                        </p>
                    )}
                </>
            )}
        </div>
    );
}

export default RSIAnalysis3;
