import React, { useEffect, useState } from "react";
import FetchMACDData from "./FetchMACDData";
import FetchStockDataForCode from "./FetchStockDataForCode";

function MACDAnalysis2({ stockCode }) {
    const [macdData, setMACDData] = useState([]);
    const [priceData, setPriceData] = useState([]);
    const [analysisText, setAnalysisText] = useState("");

    useEffect(() => {
        if (macdData.length > 0 && priceData.length > 0) {
            const analysis = performDivergenceAnalysis();
            setAnalysisText(analysis);
        }
    }, [macdData, priceData]);

    const performDivergenceAnalysis = () => {
        const period = 14; // 14일 기준

        const firstLow = findLowPoint(priceData, period, priceData.length - 1);
        const secondLow = firstLow ? findLowPoint(priceData, period, firstLow.index - 1) : null;

        if (firstLow && secondLow) {
            const firstMACDLow = macdData[firstLow.index].MACD;
            const secondMACDLow = macdData[secondLow.index].MACD;

            if (firstLow.price > secondLow.price && firstMACDLow < secondMACDLow) {
                return `상승 다이버전스 발생: 첫 번째 저점(${secondLow.date})부터 두 번째 저점(${firstLow.date})까지. 이는 강력한 매수 신호일 수 있습니다.`;
            } else if (firstLow.price < secondLow.price && firstMACDLow > secondMACDLow) {
                return `하락 다이버전스 발생: 첫 번째 고점(${secondLow.date})부터 두 번째 고점(${firstLow.date})까지. 이는 강력한 매도 신호일 수 있습니다.`;
            } else {
                return `다이버전스가 발생하지 않았습니다. 현재의 흐름은 안정적일 수 있습니다.`;
            }
        } else {
            return `충분한 데이터가 없어 다이버전스를 계산할 수 없습니다.`;
        }
    };

    const findLowPoint = (data, period, startIndex) => {
        let lowest = { price: Infinity, index: -1, date: "" };

        for (let i = startIndex; i >= 2; i--) {
            if (i < period) break;

            const a = data[i - 2];
            const b = data[i - 1];
            const c = data[i];

            // a > b < c 조건을 확인
            if (a.close > b.close && b.close < c.close) {
                if (b.close < lowest.price) {
                    lowest = { price: b.close, index: i - 1, date: b.date };
                }
            }
        }

        return lowest.index !== -1 ? lowest : null;
    };

    return (
        <div>
            <FetchMACDData stockCode={stockCode} onMACDFetch={setMACDData} />
            <FetchStockDataForCode stockCode={stockCode} onSDFCFetch={setPriceData} />
            <h4 className="analysis-text">Divergence 분석</h4>
            <p>{analysisText}</p>
        </div>
    );
}

export default MACDAnalysis2;
