import React, { useState, useEffect } from 'react';
import ADXreturn from "./ADXreturn";
import ADXCalculations2 from "./ADXCalculations2";
import ADXCalculations3 from "./ADXCalculations3";
import ADXCalculations4 from "./ADXCalculations4";

function ADXTotalMake({ stockCode, onResultUpdate }) {
    const [ADXScore1, setADXScore1] = useState(null);
    const [ADXScore2, setADXScore2] = useState(null);
    const [ADXScore3, setADXScore3] = useState(null);
    const [ADXScore4, setADXScore4] = useState(null);

    // 중요도 설정 (가중치)
    const weights = {
        adx1: 3,
        adx2: 2,
        adx3: 1,
        adx4: 2,
    };

    // 총점 계산 함수
    const calculateScores = () => {

        const totalScore = (
            ((ADXScore1.score || 0) * weights.adx1) +
            ((ADXScore2.score || 0) * weights.adx2) +
            ((ADXScore3.score || 0) * weights.adx3) +
            ((ADXScore4.score || 0) * weights.adx4)
        );

        const totalWeight = (
            (ADXScore1.score !== null ? weights.adx1 : 0) +
            (ADXScore2.score !== null ? weights.adx2 : 0) +
            (ADXScore3.score !== null ? weights.adx3 : 0) +
            (ADXScore4.score !== null ? weights.adx4 : 0)
        );

        const averageScore = totalWeight > 0 ? (totalScore / totalWeight).toFixed(2) : null;

        return averageScore;
    };

    // 추천 계산 함수
    const getRecommendation = (averageScore) => {
        if (averageScore <= 2.0) {
            return '강한 매수';
        } else if (averageScore <= 4.0) {
            return '매수';
        } else if (averageScore <= 6.0) {
            return '보통';
        } else if (averageScore <= 8.0) {
            return '매도';
        } else {
            return '강한 매도';
        }
    };

    // 값이 변할 때마다 계산 후 결과를 업데이트
    useEffect(() => {
        const averageScore = calculateScores();

        if (averageScore !== null) {
            const finalRecommendation = getRecommendation(averageScore);

            if (onResultUpdate) {
                onResultUpdate({
                    name: 'ADX(14, 14, 100, 1)',
                    value: (ADXScore1 && ADXScore1.avgADX ? ADXScore1.avgADX.toFixed(2) : '데이터 수집 중...'),
                    result: averageScore,
                    recommendation: finalRecommendation
                });
            }
        }
    }, [ADXScore1, ADXScore2, ADXScore3, ADXScore4]); // 모든 상태 변경을 감시

    return (
        <div>
            <ADXreturn stockCode={stockCode} onScoreCalculated={setADXScore1} />
            <ADXCalculations2 stockCode={stockCode} onScoreCalculated={setADXScore2} />
            <ADXCalculations3 stockCode={stockCode} onScoreCalculated={setADXScore3} />
            <ADXCalculations4 stockCode={stockCode} onScoreCalculated={setADXScore4} />
        </div>
    );
}

export default ADXTotalMake;
