import React, { useState, useEffect } from 'react';
import FetchSARData from '../ComponentsFetch/FetchParabolicSARData';

function SARAnalysis3({ stockCode }) {
    const [sarData, setSARData] = useState([]);
    const [analysisResult, setAnalysisResult] = useState('');

    // 가속 계수(AF) 변화를 계산하기 위해 서버에서 AF 값을 받아와야 합니다.
    // 따라서 계산 함수와 호출 함수에서 AF 값을 반환하도록 수정해야 합니다.

    // 수정된 calculate_parabolicSAR 함수에서는 각 데이터 포인트마다 AF 값을 저장해야 합니다.

    const analyzeTrendStrength = (sarData) => {
        if (sarData.length < 1) {
            return '데이터가 충분하지 않습니다.';
        }

        let result = '<strong>추세 강도 분석</strong><br/><br/>';

        const afValues = sarData.map((data) => data.AF);
        const currentAF = afValues[afValues.length - 1];

        result += `현재 가속 계수(AF) 값은 <strong>${currentAF}</strong>입니다.<br/>`;

        if (currentAF >= 0.2) {
            result += '→ AF가 최대치에 도달하여 추세가 강하게 진행되고 있습니다.<br/>';
        } else {
            result += '→ AF가 최대치에 도달하지 않아 추세의 강도가 중간 수준입니다.<br/>';
        }

        return result;
    };

    useEffect(() => {
        if (sarData.length > 0) {
            const result = analyzeTrendStrength(sarData);
            setAnalysisResult(result);
        }
    }, [sarData]);

    return (
        <div>
            <FetchSARData stockCode={stockCode} onSARFetch={setSARData} />
            <h4>Parabolic SAR 추세 강도 분석</h4>
            <p dangerouslySetInnerHTML={{ __html: analysisResult }}></p>
        </div>
    );
}

export default SARAnalysis3;
