import React, { useEffect, useState } from "react";
import FetchADXData from "./FetchADXData";
import FetchStockDataForCode from "./FetchStockDataForCode";
import FetchStockList from "./FetchStockList";
import Highcharts from "highcharts";
import HighchartsReact from "highcharts-react-official";

function ChartOfADX({ stockCode }) {
    const [stockData, setStockData] = useState([]);
    const [stockList, setStockList] = useState([]);
    const [ADXData, setADXData] = useState([]);
    const [options, setOptions] = useState({});

    useEffect(() => {
        if(stockData.length > 0 && stockList.length > 0 && ADXData.length > 0) {
            const ADXLine = ADXData.map(item => [new Date(item.date).getTime(), item.ADX])

            setOptions( {
                chart: {
                    type: 'line',
                    height: 210,
                },
                title: {
                    text: '',
                },
                accessibility: {
                  enabled: false
                },
                xAxis: {
                    type: 'datetime',
                    labels: {
                        formatter: function () {
                            return Highcharts.dateFormat('%m. %d', this.value);
                        }
                    },
                },
                yAxis: [{
                    title: {
                        text: '',
                    },
                }, {
                    title: {
                        text: '',
                    },
                    opposite: true,
                }],
                series: [
                    {
                        name: 'ADX Line',
                        data: ADXData,
                        color: '#0071e3'
                    },
                ],
                tooltip: {
                    shared: true,
                    crosshairs: true,
                },
                credits: {
                    enabled: false,
                },
                legend: {
                    enabled: true,
                },
            })
        }
    }, [stockData, stockList, ADXData])

    return (
        <div>
            <FetchStockDataForCode stockCode={stockCode} onSDFCFetch={setStockData}/>
            <FetchADXData stockCode={stockCode} onADXFetch={setADXData}/>
            <FetchStockList onListFetch={setStockList} />

            {options.series && options.series.length > 0 && (
                <HighchartsReact highcharts={Highcharts} options={options} />
            )}
        </div>
    )
}

export default ChartOfADX;
