import React, { useState, useEffect } from "react";
import Highcharts from "highcharts";
import HighchartsReact from "highcharts-react-official";
import HighchartsMore from "highcharts/highcharts-more"; // highcharts-more 모듈 추가
import HighchartsStock from "highcharts/modules/stock";  // Highcharts Stock 모듈 추가
import FetchStockDataForCode from "../ComponentsFetch/FetchStockDataForCode";
import FetchKeltnerData from "../ComponentsFetch/FetchKeltnerData";

// 모듈 활성화
HighchartsMore(Highcharts);
HighchartsStock(Highcharts);

function ChartOfKeltner({ stockCode }) {
    const [stockData, setStockData] = useState([]);
    const [keltnerData, setKeltnerData] = useState([]);
    const [options, setOptions] = useState({});

    useEffect(() => {
        if (stockData.length > 0 && keltnerData.length > 0 && stockCode) {
            const dataForChart = stockData
                .map(item => ({
                    date: new Date(item.date).getTime(), // timestamp로 변경
                    close: item.close,
                }))
                .sort((a, b) => new Date(a.date) - new Date(b.date));

            const upperBand = keltnerData.map(item => [new Date(item.date).getTime(), item.Upper_Band]);
            const middleLine = keltnerData.map(item => [new Date(item.date).getTime(), item.Middle_Line]);
            const lowerBand = keltnerData.map(item => [new Date(item.date).getTime(), item.Lower_Band]);

            // Set chart options
            setOptions({
                chart: {
                    type: 'line',
                    zoomType: 'x', // x축을 기준으로 확대 가능
                    height: 450,
                },
                title: {
                    text: '',
                },
                xAxis: {
                    type: 'datetime', // datetime 유형으로 변경
                    labels: {
                        format: '{value:%m-%d}', // 날짜 형식
                    },
                },
                yAxis: {
                    title: {
                        text: '',
                    },
                    lineWidth: 2,
                    opposite: true
                },
                series: [
                    {
                        name: 'Close Price',
                        data: dataForChart.map(item => [item.date, item.close]),
                        marker: {
                            enabled: false, // Disable data point markers for close price
                        },
                        color: '#0071e3',
                        lineWidth: 2,
                    },
                    {
                        name: 'Keltner Channel (Area)',
                        type: 'arearange',
                        data: upperBand.map((item, index) => [item[0], lowerBand[index][1], item[1]]),
                        color: 'rgba(211, 211, 211, 0.3)',
                        lineWidth: 0,
                        enableMouseTracking: false,
                    },
                    {
                        name: 'Upper Band',
                        data: upperBand,
                        color: '#8A8A8A', // 하늘색
                        lineWidth: 1.5,
                        marker: {
                            enabled: false,
                        },
                        dashStyle: 'solid',
                    },
                    {
                        name: 'Middle Line',
                        data: middleLine,
                        color: '#8A8A8A', // 하늘색
                        lineWidth: 1.5,
                        marker: {
                            enabled: false,
                        },
                        dashStyle: 'solid',
                    },
                    {
                        name: 'Lower Band',
                        data: lowerBand,
                        color: '#8A8A8A', // 하늘색
                        lineWidth: 1.5,
                        marker: {
                            enabled: false,
                        },
                        dashStyle: 'solid',
                    },
                ],
                legend: {
                    enabled: true, // Show legend to distinguish between lines
                },
                credits: {
                    enabled: false, // Disable the "Highcharts.com" text
                },
                tooltip: {
                    shared: true,
                    crosshairs: true,
                },
                scrollbar: {
                    enabled: true, // 하단 스크롤바 활성화
                },
                plotOptions: {
                    series: {
                        states: {
                            inactive: {
                                opacity: 1 // 마우스를 올렸을 때 다른 라인의 투명도를 1로 설정해 희미해지지 않도록 설정
                            },
                            hover: {
                                enabled: false // 마우스 호버 시 희미해지는 기능 비활성화
                            }
                        }
                    }
                },
                rangeSelector: {
                    enabled: true, // 범위 선택 옵션 활성화
                    inputEnabled: false,
                    buttons: [
                        {
                            type: 'month',
                            count: 1,
                            text: '1m'
                        },
                        {
                            type: 'month',
                            count: 3,
                            text: '3m'
                        },
                        {
                            type: 'month',
                            count: 6,
                            text: '6m'
                        },
                        {
                            type: 'ytd',
                            text: 'YTD'
                        },
                        {
                            type: 'year',
                            count: 1,
                            text: '1y'
                        },
                        {
                            type: 'all',
                            text: 'All'
                        }
                    ],
                    selected: 5 // Default 선택 범위 설정 (3개월)
                },
            });
        }
    }, [stockData, keltnerData, stockCode]);

    return (
        <div>
            <FetchStockDataForCode stockCode={stockCode} onSDFCFetch={setStockData} />
            <FetchKeltnerData stockCode={stockCode} onKelFetch={setKeltnerData} />
            <HighchartsReact highcharts={Highcharts} options={options} />
        </div>
    );
}

export default ChartOfKeltner;
