import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Login from './pages/Login';
import Signup from './pages/Signup';
import FetchStockData from './components/FetchStockData';
import FetchUserData from './components/FetchUserData';

function App() {

    const handleDataFetch = (data) => {
        setStockData(data); // 주식 데이터를 상태에 저장
    };

    const handleUserFetch = (data) => {
        setUserData(data); // 유저 데이터를 상태에 저장
    };

    return (
        <Router>
            <FetchStockData onDataFetch={handleDataFetch} />
            <FetchUserData onDataFetch={handleUserFetch} />

            <Routes>
                <Route path="/" element={<Login />} />
                <Route path="/signup" element={<Signup />} />
                <Route path="*" element={<Login />} />
            </Routes>
        </Router>
    );
}

export default App;
