import { useState, useEffect, useRef } from "react";
import { useNavigate } from 'react-router-dom';
import FetchStockList from "../ComponentsFetch/FetchStockOrignal/FetchStockList";
import FetchRealTime from "../ComponentsFetch/FetchStockOrignal/FetchRealTime";
import ChartOfClose from "../ComponentsChart/ChartOfClose";
import Navbar from "../components/Navbar";
import './Home.css'; // Make sure your CSS file is included

function Home() {
    const [stockData, setStockData] = useState([]); // Real-time data
    const [stockList, setStockList] = useState([]); // List of stocks
    const [filteredData, setFilteredData] = useState([]); // Filtered data to display
    const [selectedStock, setSelectedStock] = useState(null);
    const [selectedStockName, setSelectedStockName] = useState("");
    const navigate = useNavigate();
    const [isMenuOpen, setIsMenuOpen] = useState(false);
    const menuRef = useRef(null);

    // Handle menu click event
    useEffect(() => {
        const outSideClick = (e) => {
            const { target } = e;
            if (isMenuOpen && menuRef.current && !menuRef.current.contains(target)) {
                setIsMenuOpen(false);
            }
        };
        document.body.classList.add('home');
        document.addEventListener("mousedown", outSideClick);
        return () => {
            document.body.classList.remove('home');
            document.removeEventListener("mousedown", outSideClick);
        };
    }, [isMenuOpen]);

    // Filter and format stock data for display
    useEffect(() => {
        if (stockList.length > 0 && stockData.length > 0) {
            const formattedData = stockData
                .map(data => {
                    const matchingStock = stockList.find(stock => stock.code === data.stock_code);
                    if (matchingStock) {
                        const date = new Date(data.timestamp); // Change this to 'timestamp'
                        const formattedDate = `${(date.getMonth() + 1).toString().padStart(2, '0')}.${date.getDate().toString().padStart(2, '0')}`;
                        return {
                            ...data,
                            stock_name: matchingStock.name,
                            date: formattedDate
                        };
                    }
                    return null;
                })
                .filter(data => data !== null)
                .sort((a, b) => b.volume - a.volume); // Sort by volume in descending order

            setFilteredData(formattedData);
        }
    }, [stockList, stockData]);

    const handleStockClick = (stockCode, stockName) => {
        setSelectedStock(stockCode);
        setSelectedStockName(stockName);
    };

    return (
        <>
            <Navbar/>

            <div className='home-container'>
                <div className="data-section">
                    <FetchStockList onListFetch={setStockList}/>

                    {/* Fetch real-time data */}
                    <FetchRealTime onDataFetch={setStockData}/>
                </div>

                <div className="chart-container">
                    {selectedStock && (
                        <>
                            <h3 className="chart-container-text">{selectedStockName}</h3>
                            <ChartOfClose stockCode={selectedStock}/>
                            <button onClick={() => navigate('/detail', {
                                state: {
                                    stockCode: selectedStock,
                                    stockName: selectedStockName
                                }
                            })} className="more-button"> 더 보기
                            </button>
                            <button onClick={() => navigate('/techChart', {
                                state: {
                                    stockCode: selectedStock,
                                    stockName: selectedStockName
                                }
                            })} className="like-button"> 기술적 차트
                            </button>
                        </>
                    )}
                </div>
            </div>
        </>
    );
}

export default Home;
