import React, { useState, useEffect } from 'react';
import Highcharts from 'highcharts';
import HighchartsReact from 'highcharts-react-official';
import SolidGauge from 'highcharts/modules/solid-gauge';
import ADXTotalMake from "../ComponentsCalculations/ADXTotalMake";
import SARTotalCalculation from "../ComponentsCalculations/SARTotalCalculation";
import KeltnerTotalCalculation from "../ComponentsCalculations/KeltnerTotalCalculation";
import IchimokuTotalCalculation from "../ComponentsCalculations/IchimokuTotalCalculation";

SolidGauge(Highcharts);

function GaugeChartOfMovingAverages ({ stockCode }) {
    const [adxScore, setAdxScore] = useState(null);
    const [sarScore, setSarScore] = useState(null);
    const [keltnerScore, setKeltnerScore] = useState(null);
    const [ichimokuScore, setIchimokuScore] = useState(null);
    const [averageScore, setAverageScore] = useState(null);

    const weights = {
        adxWeights: 3,
        sarWeights: 2,
        keltnerWeights: 2,
        ichmokuWeights: 3,
    };

    useEffect(() => {
        console.log("?",adxScore, sarScore, keltnerScore, ichimokuScore);

        if(adxScore !== null && sarScore !== null && keltnerScore !== null
            && ichimokuScore !== null) {
            const totalScore = (
                (adxScore * weights.adxWeights) +
                (sarScore * weights.sarWeights) +
                (keltnerScore * weights.keltnerWeights) +
                (ichimokuScore * weights.ichmokuWeights)
            );
            console.log("?",totalScore);

            const totalWeight = (
                weights.adxWeights +
                weights.sarWeights +
                weights.keltnerWeights +
                weights.ichmokuWeights
            );

            console.log(totalWeight);

            setAverageScore((totalScore / totalWeight).toFixed(2));

            console.log(setAverageScore((totalScore / totalWeight).toFixed(2)));

        }
    }, [adxScore, sarScore, keltnerScore, ichimokuScore]);

    const chartOptions = {
        chart: {
            type: 'solidgauge',
            height: '150px',  // 세로 크기를 줄임
            width: '250px',
        },
        title: {
            text: 'Average Score',
        },
        pane: {
            startAngle: -90,
            endAngle: 90,
            background: {
                innerRadius: '60%',
                outerRadius: '100%',
                shape: 'arc',
            },
        },
        yAxis: {
            min: 0,
            max: 10, // 점수 최대값 (0-10)
            stops: [
                [0.1, '#DF5353'], // 빨강
                [0.5, '#DDDF0D'], // 노랑
                [0.9, '#55BF3B'], // 초록
            ],
            lineWidth: 0,
            tickWidth: 0,
            minorTickInterval: null,
            tickAmount: 2,
            labels: {
                y: 16,
            },
        },
        series: [
            {
                name: 'Score',
                data: [averageScore !== null ? parseFloat(averageScore) : 0],
                dataLabels: {
                    format: '<div style="text-align:center"><span style="font-size:25px">{y}</span><br/>' +
                            '<span style="font-size:12px;opacity:0.4">점수</span></div>',
                },
            },
        ],
    };

    return (
        <div>
            <ADXTotalMake stockCode={stockCode} onResultUpdate={(result) => {
                console.log(result);
                setAdxScore(result.damm);
            }}/>
            <SARTotalCalculation stockCode={stockCode} onResultUpdate={(result) => setSarScore(result.damm)}/>
            <KeltnerTotalCalculation stockCode={stockCode} onResultUpdate={(result) => setKeltnerScore(result.damm)}/>
            <IchimokuTotalCalculation stockCode={stockCode} onResultUpdate={(result) => setIchimokuScore(result.damm)}/>

            <HighchartsReact
                highcharts={Highcharts}
                options={chartOptions}
            />
        </div>
    );
}
export default GaugeChartOfMovingAverages;
