import React, { useState, useEffect } from 'react';
import Highcharts from 'highcharts';
import HighchartsReact from 'highcharts-react-official';
import SolidGauge from 'highcharts/modules/solid-gauge';
import MomentumTotalCalculation from "../ComponentsCalculations/MomentumTotalCalculation";
import StochasticTotalCalculation from "../ComponentsCalculations/StochasticTotalCalculation";
import FetchRSICalculation from "../ComponentsFetch/FetchCalculation/FetchRSICalculation";
import FetchMFICalculation from "../ComponentsFetch/FetchCalculation/FetchMFICalculation";
import FetchCCICalculation from "../ComponentsFetch/FetchCalculation/FetchCCICalculation";
import FetchMACDCalculation from "../ComponentsFetch/FetchCalculation/FetchMACDCalculation";
import FetchOscillatorsCalculation from "../ComponentsFetch/FetchCalculation/FetchOscillatorsCalculation";

SolidGauge(Highcharts);

function GaugeChartOfOscillators ({ stockCode }) {
    const [MACDscore, setMACDscore] = useState(null);
    const [CCIScore, setCCIScore] = useState(null);
    const [RSIScore, setRSIScore] = useState(null);
    const [MFIScore, setMFIScore] = useState(null);
    const [MomentumScore, setMomentumScore] = useState(null);
    const [StochasticScore, setStochasticScore] = useState(null);
    const [averageScore, setAverageScore] = useState(null);


    useEffect(() => {
        if(averageScore) {
            setMACDscore(10 - averageScore);
        }
    }, [averageScore]);

    const chartOptions = {
        chart: {
            type: 'solidgauge',
            width: 300,  // 너비 설정
            height: 200,  // 높이 설정
        },
        title: '',
        pane: {
            center: ['60%', '80%'],
            size: '100%',
            startAngle: -90,
            endAngle: 90,
            background: {
                backgroundColor: Highcharts.defaultOptions.legend.backgroundColor || '#EEE',
                innerRadius: '60%',
                outerRadius: '100%',
                shape: 'arc'
            }
        },
        tooltip: {
            enabled: false
        },
        yAxis: {
            min: 0,
            max: 10,
            stops: [
                [0.1, '#55BF3B'], // green
                [0.5, '#DDDF0D'], // yellow
                [0.9, '#DF5353'] // red
            ],
            lineWidth: 0,
            tickWidth: 0,
            minorTickInterval: null,
            tickAmount: 2,
            title: {
                text: ''
            },
            labels: {
                y: 16
            }
        },
        plotOptions: {
            solidgauge: {
                dataLabels: {
                    y: 5,
                    borderWidth: 0,
                    useHTML: true
                }
            }
        },
        series: [{
            name: 'Average Score',
            data: [parseFloat(averageScore) || 0],  // 평균 점수 반영
            dataLabels: {
                format: '<div style="text-align:center"><span style="font-size:25px">{y}</span><br/>' +
                    '<span style="font-size:12px;opacity:0.4">점수</span></div>'
            },
            tooltip: {
                valueSuffix: ' 점수'
            }
        }],
        credits: {
            enabled: false,
        },
    };

    return (
        <div>
            <FetchMACDCalculation stockCode={stockCode} onMACDCalculationFetch={(result) => setMACDscore(result.damm)} />
            <FetchCCICalculation stockCode={stockCode} onCCICalculationFetch={(result) => setCCIScore(result.damm)} />
            <FetchRSICalculation stockCode={stockCode} onRSICalculationFetch={(result) => setRSIScore(result.damm)} />
            <FetchMFICalculation stockCode={stockCode} onMFICalculationFetch={(result) => setMFIScore(result.damm)} />
            <MomentumTotalCalculation stockCode={stockCode} onResultUpdate={(result) => setMomentumScore(result.damm)} />
            <StochasticTotalCalculation stockCode={stockCode} onResultUpdate={(result) => setStochasticScore(result.damm)} />
            <FetchOscillatorsCalculation stockCode={stockCode} onOscillatorCalculationFetch={setAverageScore}/>

            <HighchartsReact
                highcharts={Highcharts}
                options={chartOptions}
            />
        </div>
    );
}

export default GaugeChartOfOscillators;
