import React, { useState, useEffect } from "react";
import Select from "react-select";
import { FixedSizeList as List } from "react-window";
import { useLocation } from 'react-router-dom';
import FetchStockDataForCode from "../components/FetchStockDataForCode";
import FetchStockList from "../components/FetchStockList";
import FetchLatestStockData from "../components/FetchLatestStockData";
import ChartOfClose from "../components/ChartOfClose";
import ChartOfRSI from "../components/ChartOfRSI";
import ChartOfMACD from "../components/ChartOfMACD";
import ChartOfADX from "../components/ChartOfADX";
import ChartOfSAR from "../components/ChartOfSAR";
import RSIAnalysis1 from "../components/RSIAnalysis1";
import RSIAnalysis2 from "../components/RSIAnalysis2";
import RSIAnalysis3 from "../components/RSIAnalysis3";
import RSIAnalysis4 from "../components/RSIAnalysis4";
import MACDAnalysis1 from "../components/MACDAnalysis1";
import MACDAnalysis2 from "../components/MACDAnalysis2";
import ADXAnalysis1 from "../components/ADXAnalysis1";
import ADXAnalysis2 from "../components/ADXAnalysis2";
import SARAnalysis1 from "../components/SARAnalysis1"
import Navbar from "../components/Navbar";
import './Detail.css';
import '../styles/LinkText.css';
import FetchRSIData from "../components/FetchRSIData";

const MenuList = ({ options, children, maxHeight, getValue }) => {
    const height = 35;
    const [value] = getValue();
    const initialOffset = options.findIndex(option => option.value === value) * height;

    return (
        <List
            height={maxHeight}
            itemCount={children.length}
            itemSize={height}
            initialScrollOffset={initialOffset}
        >
            {({ index, style }) => (
                <div style={style}>
                    {children[index]}
                </div>
            )}
        </List>
    );
};

function Detail() {
    const location = useLocation();
    const initialStockCode = location.state?.stockCode || "";
    const initialStockName = location.state?.stockName || "";
    const [stockData, setStockData] = useState([]);
    const [stockList, setStockList] = useState([]);
    const [latestData, setLatestData] = useState([]);
    const [rsiData, setRsiData] = useState([]);
    const [availableStocks, setAvailableStocks] = useState([]);
    const [selectedStock, setSelectedStock] = useState({ value: initialStockCode, label: initialStockName });
    const [filteredData, setFilteredData] = useState([]);
    const [activeTab, setActiveTab] = useState(null);
    const [inputValue, setInputValue] = useState("");

    const handleLatestStockData = (data) => {
        setLatestData(data);
    };

    const handleDataFetch = (data) => {
        setStockData(data);
    };

    const handleRsiDataFetch = (data) => {
        setRsiData(data);
    };

    const handleListFetch = (data) => {
        setStockList(data);
    };

    const handleTabClick = (tab) => {
        setActiveTab(tab);
    };

    useEffect(() => {
        if (stockData.length > 0 && stockList.length > 0) {
            const available = stockList.filter(code => latestData.stock_code === code.stockCode)
                .map(stock => ({
                    value: stock.code,
                    label: `${stock.name}`
                }));
            setAvailableStocks(available);
        }
    }, [stockData, stockList, latestData]);

    useEffect(() => {
        if (stockData.length > 0 && selectedStock) {
            const filtered = stockData.sort((b, a) => new Date(a.date) - new Date(b.date));
            setFilteredData(filtered);
        }
    }, [stockData, selectedStock]);

    const handleSelectChange = (selectedOption) => {
        setSelectedStock(selectedOption);
    };

    const customStyles = {
        option: (provided) => ({
            ...provided,
            whiteSpace: 'nowrap',
            fontSize: '14px',
            overflow: 'hidden',
            textOverflow: 'ellipsis',
            height: '2.1em',
        }),
        container: (provided) => ({
            ...provided,
            width: '450px'
        })
    };

    return (
        <>
            <Navbar />

            <div className="detail-container">
                <div className="search-bar">
                    <Select
                        components={{ MenuList }}
                        options={availableStocks}
                        onChange={handleSelectChange}
                        value={selectedStock}
                        placeholder="종목을 검색하세요"
                        styles={customStyles}
                        inputValue={inputValue}
                        onInputChange={(value) => setInputValue(value)}
                    />
                </div>

                <div className="indicator-tabs">
                    <button className={activeTab === 'RSI' ? 'active-tab' : ''}
                            onClick={() => handleTabClick('RSI')}>RSI
                    </button>
                    <button className={activeTab === 'MFI' ? 'active-tab' : ''}
                            onClick={() => handleTabClick('MFI')}>MFI
                    </button>
                    <button className={activeTab === 'MACD' ? 'active-tab' : ''}
                            onClick={() => handleTabClick('MACD')}>MACD
                    </button>
                    <button className={activeTab === 'ADX' ? 'active-tab' : ''}
                            onClick={() => handleTabClick('ADX')}>ADX
                    </button>
                    <button className={activeTab === 'Parabolic SAR' ? 'active-tab' : ''}
                            onClick={() => handleTabClick('Parabolic SAR')}>Parabolic SAR
                    </button>
                    <button className={activeTab === 'CCI' ? 'active-tab' : ''}
                            onClick={() => handleTabClick('CCI')}>CCI
                    </button>
                    <button className={activeTab === 'Keltner Channel' ? 'active-tab' : ''}
                            onClick={() => handleTabClick('Keltner Channel')}>Keltner Channel
                    </button>
                    <button className={activeTab === 'OBV' ? 'active-tab' : ''}
                            onClick={() => handleTabClick('OBV')}>OBV
                    </button>
                </div>

                <div className="detail-main-content">
                    <div className="detail-chart">
                        <FetchStockDataForCode stockCode={selectedStock.value} onSDFCFetch={handleDataFetch}/>
                        <FetchRSIData stockCode={selectedStock.value} onRSIFetch={handleRsiDataFetch}/>
                        <FetchStockList onListFetch={handleListFetch}/>
                        <FetchLatestStockData onLSDFetch={handleLatestStockData}/>

                        {selectedStock && (
                            <>
                                <h3 className="detail-chart-text">{selectedStock.label} 차트</h3>

                                {activeTab !== "Parabolic SAR" && (
                                    <ChartOfClose stockCode={selectedStock.value}/>
                                )}

                                {activeTab === "RSI" && (
                                    <ChartOfRSI stockCode={selectedStock.value}/>
                                )}
                                {activeTab === "MACD" && (
                                    <ChartOfMACD stockCode={selectedStock.value}/>
                                )}
                                {activeTab === "ADX" && (
                                    <ChartOfADX stockCode={selectedStock.value}/>
                                )}
                                {activeTab === "Parabolic SAR" && (
                                    <ChartOfSAR stockCode={selectedStock.value}/>
                                )}
                                {activeTab === "MFI" && (
                                    <ChartOfMACD stockCode={selectedStock.value} />
                                )}
                                {activeTab === "CCI" && (
                                    <ChartOfMACD stockCode={selectedStock.value} />
                                )}
                                {activeTab === "Keltner Channel" && (
                                    <ChartOfMACD stockCode={selectedStock.value} />
                                )}
                                {activeTab === "OBV" && (
                                    <ChartOfMACD stockCode={selectedStock.value} />
                                )}
                            </>
                        )}
                    </div>

                    <div className="analysis-display">
                        <h3 className="analysis-display-main-text">{selectedStock.label} 분석</h3>

                        {/* Conditionally render analysis based on availability of data */}
                        {activeTab === "RSI" && rsiData.length > 0 && (
                            <>
                                <div className="textbox">
                                    <RSIAnalysis1 stockCode={selectedStock.value} />
                                </div>
                                <div className="textbox">
                                    <RSIAnalysis2 stockCode={selectedStock.value} stockData={stockData} rsiData={rsiData} />
                                </div>
                                <div className="textbox">
                                    <RSIAnalysis3 rsiData={rsiData} />
                                </div>
                                <div className="textbox">
                                    <RSIAnalysis4 rsiData={rsiData} />
                                </div>
                            </>
                        )}

                        {activeTab === "MACD" && stockData.length > 0 && (
                            <>
                                <div className="textbox">
                                    <MACDAnalysis1 stockCode={selectedStock.value} />
                                </div>
                                <div className="textbox">
                                    <MACDAnalysis2 stockCode={selectedStock.value} />
                                </div>
                            </>
                        )}

                        {activeTab === "ADX" && stockData.length > 0 && (
                            <>
                                <div className="textbox">
                                    <ADXAnalysis1 stockCode={selectedStock.value} />
                                </div>
                                <div className="textbox">
                                    <ADXAnalysis2 stockCode={selectedStock.value} />
                                </div>
                            </>
                        )}
                        {activeTab === "Parabolic SAR" && stockData.length > 0 && (
                            <>
                                <div className="textbox">
                                    <SARAnalysis1 stockCode={selectedStock.value} />
                                </div>

                            </>
                        )}
                    </div>
                </div>

                <div className="detail-table">
                    <h3>{selectedStock ? selectedStock.label : "종목"} 종목 데이터</h3>
                    <table>
                        <thead>
                            <tr>
                                <th>일자</th>
                                <th>종가</th>
                                <th>고가</th>
                                <th>저가</th>
                                <th>시가</th>
                                <th>거래량</th>
                            </tr>
                        </thead>
                        <tbody>
                            {filteredData.map((data, index) => (
                                <tr key={index}>
                                    <td>{data.date}</td>
                                    <td>{data.close}</td>
                                    <td>{data.high}</td>
                                    <td>{data.low}</td>
                                    <td>{data.open}</td>
                                    <td>{data.volume}</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            </div>
        </>
    );
}

export default Detail;
