import React, { useState, useEffect } from 'react';
import Highcharts from 'highcharts';
import HighchartsReact from 'highcharts-react-official';
import SolidGauge from 'highcharts/modules/solid-gauge';
import SARTotalCalculation from "../ComponentsCalculations/SARTotalCalculation";
import KeltnerTotalCalculation from "../ComponentsCalculations/KeltnerTotalCalculation";
import IchimokuTotalCalculation from "../ComponentsCalculations/IchimokuTotalCalculation";
import FetchADXCalculation from "../ComponentsFetch/FetchCalculation/FetchADXCalculation";
import FetchSARCalculation from "../ComponentsFetch/FetchCalculation/FetchSARCalculation";
import FetchKeltnerCalculation from "../ComponentsFetch/FetchCalculation/FetchKeltnerCalculation";
import FetchIchimokuCalculation from "../ComponentsFetch/FetchCalculation/FetchIchimokuCalculation";

SolidGauge(Highcharts);

function GaugeChartOfMovingAverages ({ stockCode }) {
    const [adxScore, setAdxScore] = useState(null);
    const [sarScore, setSarScore] = useState(null);
    const [keltnerScore, setKeltnerScore] = useState(null);
    const [ichimokuScore, setIchimokuScore] = useState(null);
    const [averageScore, setAverageScore] = useState(null);

    const weights = {
        adxWeights: 3,
        sarWeights: 2,
        keltnerWeights: 2,
        ichmokuWeights: 3,
    };

    useEffect(() => {

        if(adxScore !== null && sarScore !== null && keltnerScore !== null
            && ichimokuScore !== null) {
            const totalScore = (
                (adxScore * weights.adxWeights) +
                (sarScore * weights.sarWeights) +
                (keltnerScore * weights.keltnerWeights) +
                (ichimokuScore * weights.ichmokuWeights)
            );

            const totalWeight = (
                weights.adxWeights +
                weights.sarWeights +
                weights.keltnerWeights +
                weights.ichmokuWeights
            );

            setAverageScore(10 - (totalScore / totalWeight).toFixed(2));

        }
    }, [adxScore, sarScore, keltnerScore, ichimokuScore]);

    const chartOptions = {
        chart: {
            type: 'solidgauge',
            width: 300,  // 너비 설정
            height: 200,  // 높이 설정
        },
        title: {
            text: '',
        },
        pane: {
            center: ['40%', '80%'],
            size: '100%',
            startAngle: -90,
            endAngle: 90,
            background: {
                backgroundColor: Highcharts.defaultOptions.legend.backgroundColor || '#EEE',
                innerRadius: '60%',
                outerRadius: '100%',
                shape: 'arc'
            }
        },
        yAxis: {
            min: 0,
            max: 10, // 점수 최대값 (0-10)
            stops: [
                [0.1, '#DF5353'], // 빨강
                [0.5, '#DDDF0D'], // 노랑
                [0.9, '#55BF3B'], // 초록
            ],
            lineWidth: 0,
            tickWidth: 0,
            minorTickInterval: null,
            tickAmount: 2,
            title: {
                text: ''
            },
            labels: {
                y: 16,
            },
        },
        plotOptions: {
            solidgauge: {
                dataLabels: {
                    y: 5,
                    borderWidth: 0,
                    useHTML: true
                }
            }
        },
        series: [{
            name: 'Average Score',
            data: [parseFloat(averageScore) || 0],  // 평균 점수 반영
            dataLabels: {
                format: '<div style="text-align:center"><span style="font-size:25px">{y}</span><br/>' +
                    '<span style="font-size:12px;opacity:0.4">점수</span></div>'
            },
            tooltip: {
                valueSuffix: ' 점수'
            }
        }],
        credits: {
            enabled: false,
        },
    };

    return (
        <div>
            <FetchADXCalculation stockCode={stockCode} onADXCalculationFetch={(result) => setAdxScore(result.damm)}/>
            <FetchSARCalculation stockCode={stockCode} onSARCalculationFetch={(result) => setSarScore(result.damm)}/>
            <FetchKeltnerCalculation stockCode={stockCode} onKeltnerCalculationFetch={(result) => setKeltnerScore(result.damm)}/>
            <FetchIchimokuCalculation stockCode={stockCode} onIchimokuCalculationFetch={(result) => setIchimokuScore(result.damm)}/>

            <HighchartsReact
                highcharts={Highcharts}
                options={chartOptions}
            />
        </div>
    );
}
export default GaugeChartOfMovingAverages;
