import React, { useEffect, useState } from "react";
import FetchADXData from "../ComponentsFetch/FetchADXData";
import FetchStockDataForCode from "../ComponentsFetch/FetchStockDataForCode";
import FetchStockList from "../ComponentsFetch/FetchStockList";
import Highcharts from "highcharts";
import HighchartsReact from "highcharts-react-official";
import HighchartsStock from "highcharts/modules/stock";

HighchartsStock(Highcharts);

function ChartOfADX({ stockCode }) {
    const [stockData, setStockData] = useState([]);
    const [stockList, setStockList] = useState([]);
    const [ADXData, setADXData] = useState([]);
    const [options, setOptions] = useState({});

    useEffect(() => {
        const [stockData, setStockData] = useState([]);
        const [ADXData, setADXData] = useState([]);
        const [options, setOptions] = useState({});

        if(stockData.length > 0 && stockList.length > 0 && ADXData.length > 0) {
            const formattedADXData = ADXData.map(item => {
                return [
                    new Date(item.date).getTime(),
                    item.ADX
                ];
            });

            const formattedClosePriceData = stockData.map(item => ({
                x: new Date(item.date).getTime(),
                y: item.close
            }));

            const formattedVolumeData = stockData.map(item => ({
                x: new Date(item.date).getTime(),
                y: item.volume,
            }));

            setOptions( {
                chart: {
                    height: 600,
                    spacing: [10, -70, 10, 10],
                    zoomType: 'x',
                    events: {
                        render: function () {
                            this.yAxis[0].axisTitle.attr({
                                x: this.plotLeft +40,
                                y: this.plotTop +20
                            });

                            this.yAxis[1].axisTitle.attr({
                                x: this.plotLeft + 30,
                                y: this.plotTop + (this.plotHeight * 0.55) + 7
                            });

                            this.yAxis[2].axisTitle.attr({
                                x: this.plotLeft + 38,
                                y: this.plotTop + (this.plotHeight * 0.85) -40
                            });
                        }
                    }
                },
                title: {
                    text: '',
                },
                accessibility: {
                  enabled: false
                },
                xAxis: {
                    type: 'datetime',
                    labels: {
                        format: '{value:%m. %d}', // 날짜 형식
                    },
                    crosshair: true,
                },
                yAxis: [
                    {
                        title: {
                            text: 'Close Price',
                            style: {
                                fontSize: '12px',
                            },
                            rotation: 0,
                        },
                        height: '50%',
                        lineWidth:2,
                        opposite: true,
                    },
                    {
                        title: {
                            text: 'Volume',
                            style: {
                                fontSize: '12px',
                            },
                            rotation: 0,
                        },
                        top: '52%',
                        height: '18%',
                        offset: 0,
                        lineWidth: 2,
                        labels: {
                            enabled: false // 볼륨에 라벨 비활성화
                        },
                        opposite: true,

                    },
                ],
                series: [
                    {
                        name: 'ADX Line',
                        data: ADXLine,
                        color: '#FF4500'
                    },
                ],
                tooltip: {
                    shared: true,
                    crosshairs: true,
                },
                credits: {
                    enabled: false,
                },
                legend: {
                    enabled: true,
                },
            })
        }
    }, [stockData, stockList, ADXData])

    return (
        <div>
            <FetchStockDataForCode stockCode={stockCode} onSDFCFetch={setStockData}/>
            <FetchADXData stockCode={stockCode} onADXFetch={setADXData}/>
            <FetchStockList onListFetch={setStockList} />

            {options.series && options.series.length > 0 && (
                <HighchartsReact highcharts={Highcharts} options={options} />
            )}
        </div>
    )
}

export default ChartOfADX;
