import React, {useState} from 'react';
import RSIAnalysis1 from "../ComponentsAnalysis/RSIAnalysis1";

function CombinedAnalysis({ stockCode }) {
    const [rsiScore, setRsiScore] = useState(null);

    const totalScore = rsiScore;

    const getRecommendation = (totalScore) => {
        console.log(totalScore);
        if (totalScore <= 2.0) {
            return '강한 매수';
        } else if (totalScore <= 4.0) {
            return '매수';
        } else if (totalScore <= 6.0) {
            return '보통';
        } else if (totalScore <= 8.0) {
            return '매도';
        } else {
            return '강한 매도';
        }
    };

    const finalRecommendation = getRecommendation(totalScore);

    return (
        <div>
            <h3>종합 분석 결과</h3>
            <RSIAnalysis1 stockCode={stockCode} onScoreCalculated={setRsiScore}/>
            <p>최종 추천: {finalRecommendation}</p>
        </div>
    )
}
export default CombinedAnalysis;
