import React, { useState, useEffect } from 'react';
import FetchMACDData from '../ComponentsFetch/FetchMACDData';

function MACDAnalysis4({ stockCode }) {
    const [macdData, setMACDData] = useState([]);
    const [analysisResult, setAnalysisResult] = useState('');

    const analyzeMultiTimeframeMACD = (macdData) => {
        if (macdData.length === 0) {
            return '데이터가 충분하지 않습니다.';
        }

        const lastIndex = macdData.length - 1;
        const currentData = macdData[lastIndex];

        // 단기 MACD 설정 (예: 12,26,9)
        const shortMACD = currentData['MACD_12_26_9'];
        const shortSignal = currentData['Signal_12_26_9'];

        // 장기 MACD 설정 (예: 50,100,9)
        const longMACD = currentData['MACD_50_100_9'];
        const longSignal = currentData['Signal_50_100_9'];

        let result = '';

        // 단기 MACD 분석
        if (shortMACD > shortSignal) {
            result += '<strong>단기 MACD (12, 26, 9):</strong> 상승 추세 (MACD Line이 Signal Line 위에 있습니다).<br/>';
        } else {
            result += '<strong>단기 MACD (12, 26, 9):</strong> 하락 추세 (MACD Line이 Signal Line 아래에 있습니다).<br/>';
        }

        // 장기 MACD 분석
        if (longMACD > longSignal) {
            result += '<strong>장기 MACD (50, 100, 9):</strong> 상승 추세 (MACD Line이 Signal Line 위에 있습니다).<br/>';
        } else {
            result += '<strong>장기 MACD (50, 100, 9):</strong> 하락 추세 (MACD Line이 Signal Line 아래에 있습니다).<br/>';
        }

        // 추세 일치 여부 확인
        if (shortMACD > shortSignal) {
            if (longMACD > longSignal) {
                result += '단기 및 장기 모두 상승 추세에 있습니다. 이는 강한 상승 모멘텀이 유지되고 있음을 의미하며, 매수 기회를 고려할 수 있습니다.<br/>';
            } else {
                result += '단기 상승 추세이지만 장기적으로는 하락 추세입니다. 이는 일시적인 반등일 가능성이 있으며, 장기 하락세 속에서 단기 조정이 발생할 수 있습니다. 매수 시점에 대한 신중한 검토가 필요합니다.<br/>';
            }
        } else {
            if (longMACD > longSignal) {
                result += '단기 하락 추세이지만, 장기적으로는 상승 추세입니다. 이는 장기 상승세가 유지되면서도 단기적으로 조정이 일어나고 있을 수 있으며, 이 경우 하락이 멈추면 매수 기회를 찾을 수 있습니다.<br/>';
            } else {
                result += '단기 및 장기 모두 하락 추세에 있습니다. 이는 강한 하락 모멘텀이 지속되고 있음을 의미하며, 매도 기회를 고려할 수 있습니다.<br/>';
            }
        }

        return result;
    };

    useEffect(() => {
        if (macdData.length > 0) {
            const result = analyzeMultiTimeframeMACD(macdData);
            setAnalysisResult(result);
        }
    }, [macdData]);

    return (
        <div>
            <FetchMACDData stockCode={stockCode} onMACDFetch={setMACDData} />
            <h4>다중 기간 MACD 분석</h4>
            <p dangerouslySetInnerHTML={{ __html: analysisResult }}></p>
        </div>
    );
}

export default MACDAnalysis4;
