import React, { useState, useEffect } from "react";
import Highcharts from "highcharts";
import FetchStockDataForCode from "./FetchStockDataForCode";
import HighchartsReact from "highcharts-react-official";


function ChartOfClose({ stockCode }) {
    const [stockData, setStockData] = useState([]);
    const [filteredData, setFilteredData] = useState([]);
    const [options, setOptions] = useState({});

    useEffect(() => {
        if (stockData.length > 0 && stockCode) {
            const dataForChart = stockData
                .filter(item => item.stock_code === stockCode)
                .map(item => ({
                    date: new Date(item.date).toLocaleDateString('ko-KR', { month: '2-digit', day: '2-digit' }),
                    close: item.close,
                }))
                .sort((a, b) => new Date(a.date) - new Date(b.date));

            setFilteredData(dataForChart);

            // Set chart options
            setOptions({
                chart: {
                    type: 'line',
                    height: 330,
                },
                title: {
                    text: '',
                },
                xAxis: {
                    categories: dataForChart.map(item => item.date),
                    labels: {
                        formatter: function () {
                            return this.value; // Keeping date format MM/DD
                        },
                    },
                },
                yAxis: {
                    title: {
                        text: '',
                    },
                },
                series: [
                    {
                        name: 'Close Price',
                        data: dataForChart.map(item => item.close),
                        marker: {
                            enabled: false, // Disable data point markers
                        },
                        color: '#0071e3',
                    },
                ],
                legend: {
                    enabled: true,
                },
                credits: {
                    enabled: false, // Disable the "Highcharts.com" text
                },
                tooltip: {
                    shared: true,
                    crosshairs: true,
                },
            });
        }
    }, [stockData, stockCode]);

    if (!stockCode) return null;

    return (
        <div>
            <HighchartsReact highcharts={Highcharts} options={options} />
        </div>
    );
}

export default ChartOfClose;
