import React, { useState, useEffect } from 'react';
import FetchADXData from '../ComponentsFetch/FetchADXData';

function ADXAnalysis4({ stockCode }) {
    const [adxData, setADXData] = useState([]);
    const [analysisResult, setAnalysisResult] = useState('');

    const analyzeCombinedADX = (adxData) => {
        if (adxData.length === 0) {
            return '데이터가 충분하지 않습니다.';
        }

        const lastData = adxData[adxData.length - 1];
        const adxValue = lastData.ADX;
        const diPlus = lastData.DI14Plus;
        const diMinus = lastData.DI14Minus;

        let result = '<strong>종합 분석</strong><br/><br/>';

        result += `현재 ADX 값은 <strong>${adxValue.toFixed(2)}</strong>입니다.<br/>`;
        result += `DI+ 값은 <strong>${diPlus.toFixed(2)}</strong>, DI- 값은 <strong>${diMinus.toFixed(2)}</strong>입니다.<br/><br/>`;

        if (adxValue >= 25) {
            if (diPlus > diMinus) {
                result += '→ ADX가 높고 DI+ > DI-이므로 <strong>강한 상승 추세</strong>입니다.<br/>';
                result += '매수 기회를 고려해볼 수 있습니다.<br/>';
            } else if (diPlus < diMinus) {
                result += '→ ADX가 높고 DI+ < DI-이므로 <strong>강한 하락 추세</strong>입니다.<br/>';
                result += '매도 또는 관망을 고려해볼 수 있습니다.<br/>';
            } else {
                result += '→ ADX가 높지만 DI+와 DI-이 동일하여 방향성을 판단하기 어렵습니다.<br/>';
            }
        } else if (adxValue <= 20) {
            result += '→ ADX가 낮아 추세가 약합니다. 횡보장으로 판단되며, 신중한 접근이 필요합니다.<br/>';
        } else {
            result += '→ 추세의 강도가 애매하므로 신중한 접근이 필요합니다.<br/>';
        }

        return result;
    };

    useEffect(() => {
        if (adxData.length > 0) {
            const result = analyzeCombinedADX(adxData);
            setAnalysisResult(result);
        }
    }, [adxData]);

    return (
        <div>
            <FetchADXData stockCode={stockCode} onADXFetch={setADXData} />
            <h4>ADX 종합 분석</h4>
            <p dangerouslySetInnerHTML={{ __html: analysisResult }}></p>
        </div>
    );
}

export default ADXAnalysis4;
