import React, { useState, useEffect } from "react";
import { useLocation } from 'react-router-dom';
import FetchStockData from "../components/FetchStockData";
import FetchStockList from "../components/FetchStockList";
import ChartOfClose from "../components/ChartOfClose";
import './Detail.css';
import ProfileIcon from '../assets/profile.png';

function Detail() {
    const location = useLocation();
    const { stockCode, stockName } = location.state || {};
    const [stockData, setStockData] = useState([]);
    const [stockList, setStockList] = useState([]);
    const [filteredData, setFilteredData] = useState([]);
    const [isMenuOpen, setIsMenuOpen] = useState(false);
    const [searchTerm, setSearchTerm] = useState("");
    const [availableStocks, setAvailableStocks] = useState([]);

    const toggleMenu = () => {
        setIsMenuOpen(!isMenuOpen);
    };

    // FetchStockData에서 모든 데이터를 가져옴
    const handleDataFetch = (data) => {
        setStockData(data); // 전체 데이터를 state에 저장
    };

    // FetchStockList에서 종목 목록을 가져옴
    const handleListFetch = (list) => {
        setStockList(list);
    };

    useEffect(() => {
        if (stockData.length > 0 && stockList.length > 0) {
            // 교집합을 찾아내기
            const available = stockList.filter(stock =>
                stockData.some(data => data.stock_code === stock.code)
            );
            setAvailableStocks(available);
        }
    }, [stockData, stockList]);

    useEffect(() => {
        if (stockData.length > 0 && stockCode) {
            const filtered = stockData.filter(data => data.stock_code === stockCode)
                                      .map(data => ({
                                          date: data.date,
                                          close: data.close,
                                          high: data.high,
                                          low: data.low,
                                          open: data.open,
                                          volume: data.volume
                                      }));
            setFilteredData(filtered); // 필터링된 데이터를 저장
        }
    }, [stockData, stockCode]);

    const handleSearchChange = (e) => {
        setSearchTerm(e.target.value);
    };

    const handleStockSelect = (selectedStockCode) => {
        const selectedStock = availableStocks.find(stock => stock.code === selectedStockCode);
        if (selectedStock) {
            // 선택한 종목으로 이동하거나 상태를 업데이트
            // 예를 들어:
            window.location.href = `/detail?stockCode=${selectedStock.code}&stockName=${encodeURIComponent(selectedStock.name)}`;
        }
    };

    return (
        <>
            <div className='navbar'>
                <h2 className="navbar-title">ProStock</h2>
                <div className="profile-container" onClick={toggleMenu}>
                    <img src={ProfileIcon} alt="Profile" className="profile-icon"/>
                    {isMenuOpen && (
                        <div className="profile-menu">
                            <p>프로필</p>
                            <p>설정</p>
                            <p>로그아웃</p>
                        </div>
                    )}
                </div>
            </div>

            <div className="detail-container">
                {/* 검색 및 드롭다운 추가 */}
                <div className="search-bar">
                    <input
                        type="text"
                        value={searchTerm}
                        onChange={handleSearchChange}
                        placeholder="종목을 검색하세요..."
                    />
                    <select onChange={(e) => handleStockSelect(e.target.value)}>
                        <option value="">종목을 선택하세요</option>
                        {availableStocks.filter(stock =>
                            stock.name.includes(searchTerm) || stock.code.includes(searchTerm)
                        ).map(stock => (
                            <option key={stock.code} value={stock.code}>
                                {stock.name} ({stock.code})
                            </option>
                        ))}
                    </select>
                </div>

                <div className="detail-main-content">
                    <div className="detail-chart">
                        <FetchStockData onDataFetch={handleDataFetch}/>
                        <h3 className="detail-chart-text">{stockName} 주가 차트</h3>
                        <ChartOfClose stockCode={stockCode}/>
                    </div>

                    <div className="analysis-display">
                        <h3>분석</h3>
                        <div className="textbox">
                            <h4 className="analysis-text">분석 1</h4>
                            <p>분석 내용...</p>
                        </div>
                    </div>
                </div>

                <div className="detail-table">
                    <h3>{stockName} 종목 데이터</h3>
                    <table>
                        <thead>
                        <tr>
                            <th>일자</th>
                            <th>종가</th>
                            <th>고가</th>
                            <th>저가</th>
                            <th>시가</th>
                            <th>거래량</th>
                        </tr>
                        </thead>
                        <tbody>
                        {filteredData.map((data, index) => (
                            <tr key={index}>
                                <td>{data.date}</td>
                                <td>{data.close}</td>
                                <td>{data.high}</td>
                                <td>{data.low}</td>
                                <td>{data.open}</td>
                                <td>{data.volume}</td>
                            </tr>
                        ))}
                        </tbody>
                    </table>
                </div>
            </div>
        </>
    );
}

export default Detail;
