import React, { useState, useEffect } from 'react';
import FetchADXData from '../ComponentsFetch/FetchADXData';

function ADXAnalysis2({ stockCode }) {
    const [adxData, setADXData] = useState([]);
    const [analysisResult, setAnalysisResult] = useState('');

    const analyzeDICrossovers = (adxData) => {
        if (adxData.length < 2) {
            return '데이터가 충분하지 않습니다.';
        }

        const lastIndex = adxData.length - 1;
        const currentData = adxData[lastIndex];
        const prevData = adxData[lastIndex - 1];

        const currentDIPlus = currentData.DI14Plus;
        const currentDIMinus = currentData.DI14Minus;
        const prevDIPlus = prevData.DI14Plus;
        const prevDIMinus = prevData.DI14Minus;

        let result = '<strong>DI+와 DI- 교차 분석</strong><br/><br/>';

        if (prevDIPlus <= prevDIMinus && currentDIPlus > currentDIMinus) {
            result += '매수 신호 발생: DI+가 DI-를 상향 돌파했습니다.<br/>';
        } else if (prevDIPlus >= prevDIMinus && currentDIPlus < currentDIMinus) {
            result += '매도 신호 발생: DI+가 DI-를 하향 돌파했습니다.<br/>';
        } else {
            result += 'DI+와 DI- 사이에 특별한 교차 신호가 없습니다.<br/>';
        }

        return result;
    };

    useEffect(() => {
        if (adxData.length > 0) {
            const result = analyzeDICrossovers(adxData);
            setAnalysisResult(result);
        }
    }, [adxData]);

    return (
        <div>
            <FetchADXData stockCode={stockCode} onADXFetch={setADXData} />
            <h4>DI+와 DI- 교차 분석</h4>
            <p dangerouslySetInnerHTML={{ __html: analysisResult }}></p>
        </div>
    );
}

export default ADXAnalysis2;
