import React, { useState, useEffect } from 'react';
import FetchMACDData from '../ComponentsFetch/FetchMACDData';

function MACDAnalysis3({ stockCode }) {
    const [macdData, setMACDData] = useState([]);
    const [analysisResult, setAnalysisResult] = useState('');

    const analyzeMACDHistogram = (macdData, period = 14) => {
        if (macdData.length < 2) {
            return '데이터가 충분하지 않습니다.';
        }

        const lastIndex = macdData.length - 1;
        const currentHistogram = macdData[lastIndex].Histogram;
        const prevHistogram = macdData[lastIndex - 1].Histogram;

        let result = '';

        // Histogram의 0선 돌파 여부를 먼저 분석
        if (prevHistogram < 0 && currentHistogram > 0) {
            result += '매수 모멘텀이 강화되고 있습니다. Histogram이 0선을 상향 돌파했습니다.<br/>';
        } else if (prevHistogram > 0 && currentHistogram < 0) {
            result += '매도 모멘텀이 강화되고 있습니다. Histogram이 0선을 하향 돌파했습니다.<br/>';
        } else {
            result += 'Histogram이 0선 근처에서 움직이고 있습니다.<br/>';
        }


        // 3. 추가 분석: 히스토그램의 최근 14일간 트렌드 분석
        const histogramTrend = macdData.slice(lastIndex - 5 + 1, lastIndex + 1).map(day => day.Histogram);
        const histogramTrendDirection14 = histogramTrend[histogramTrend.length - 1] - histogramTrend[0];

        if (histogramTrendDirection14 > 0) {
            result += '추가 분석: 히스토그램이 최근 5일간 증가하고 있어 모멘텀이 강해지고 있습니다. 상승 추세가 지속될 가능성이 있습니다.<br/>';
        } else if (histogramTrendDirection14 < 0) {
            result += '추가 분석: 히스토그램이 최근 5일간 감소하고 있어 모멘텀이 약화되고 있습니다. 하락 추세로 전환될 수 있습니다.<br/>';
        }

        return result;
    };

    useEffect(() => {
        if (macdData.length > 0) {
            const result = analyzeMACDHistogram(macdData);
            setAnalysisResult(result);
        }
    }, [macdData]);

    return (
        <div>
            <FetchMACDData stockCode={stockCode} onMACDFetch={setMACDData} />
            <h4>MACD Histogram 분석</h4>
            <p dangerouslySetInnerHTML={{ __html: analysisResult }}></p>
        </div>
    );
}

export default MACDAnalysis3;
