import React, { useState, useEffect } from 'react';
import FetchMFIData from './FetchMFIData';
import FetchStockDataForCode from './FetchStockDataForCode';

function MFIAnalysis1({ stockCode }) {
    const [mfiData, setMFIData] = useState([]);
    const [stockData, setStockData] = useState([]);
    const [analysisResult, setAnalysisResult] = useState('');

    // MFI 점수 및 분석
    const analyzeMFI = (data) => {
        let lastIndex = data.length - 1;
        if (lastIndex < 1) return '데이터가 충분하지 않습니다.';

        const currentMFI = data[lastIndex].MFI.toFixed(2);
        const prevMFI = data[lastIndex - 1]?.MFI || 0;
        let result = '';

        // MFI > 50인 경우 (강세 시장)
        if (currentMFI > 50) {
            if (currentMFI > prevMFI) {
                if (currentMFI > 70 && currentMFI < 80) {
                    result += `강세 시장입니다. 매수세가 강화되고 있으며, MFI는 ${currentMFI}로 과매수 구간에 가까워지고 있습니다. 주의가 필요합니다. <br /><br />`;
                } else if (currentMFI >= 80) {
                    result += `과매수 구간에 진입했습니다. MFI는 ${currentMFI}이며, 상승 여력이 줄어들 수 있습니다. 주의하세요. <br /><br />`;
                } else {
                    result += `매수세가 강해지고 있으며, MFI는 ${currentMFI}로 추가 상승 가능성이 높습니다. <br /><br />`;
                }
            } else {
                result += `강세 시장이지만, MFI가 ${currentMFI}로 하락 중입니다. 상승세가 약해질 가능성이 있습니다. 신중히 검토하세요. <br /><br />`;
            }
        }
        // MFI < 50인 경우 (약세 시장)
        else {
            if (currentMFI > prevMFI) {
                if (currentMFI > 30 && currentMFI < 50) {
                    result += `약세 시장에서 반등 조짐이 보이고 있습니다. MFI는 ${currentMFI}이며, 상승 가능성을 주시하세요. <br /><br />`;
                } else if (currentMFI <= 20) {
                    result += `과매도 구간에서 상승 중입니다. MFI는 ${currentMFI}이며, 반등 가능성이 큽니다. <br /><br />`;
                } else {
                    result += `약세가 지속되고 있지만, MFI는 ${currentMFI}로 상승 가능성이 보입니다. <br /><br />`;
                }
            } else {
                result += `MFI가 하락 중이며, 약세가 지속될 가능성이 큽니다. MFI는 ${currentMFI}입니다. <br /><br />`;
            }
        }

        return result;
    };

    useEffect(() => {
        if (mfiData.length > 0) {
            const result = analyzeMFI(mfiData);
            setAnalysisResult(result);
        }
    }, [mfiData]);

    return (
        <div>
            <FetchMFIData stockCode={stockCode} onMFIFetch={setMFIData} />
            <FetchStockDataForCode stockCode={stockCode} onSDFCFetch={setStockData} />
            <h4 className="analysis-text">MFI 분석</h4>
            <p dangerouslySetInnerHTML={{ __html: analysisResult.replace(/\n/g, '<br />') }}></p>
        </div>
    );
}

export default MFIAnalysis1;
