import React, { useState, useEffect } from 'react';
import FetchMFIData from '../ComponentsFetch/FetchMFIData';
import FetchStockDataForCode from '../ComponentsFetch/FetchStockDataForCode';

function MFIAnalysis1({ stockCode }) {
    const [mfiData, setMFIData] = useState([]);
    const [stockData, setStockData] = useState([]);
    const [analysisResult, setAnalysisResult] = useState('');

    const analyzeMFITrend = (mfiData, stockData, period = 14) => {
        console.log('mfiData:', mfiData);

        let lastIndex = mfiData.length - 1;
        if (lastIndex < period || stockData.length < period) return '데이터가 충분하지 않습니다.';

        // 현재 및 이전 MFI 값들
        console.log('mfiData[lastIndex]:', mfiData[lastIndex]);

        const currentMFI = mfiData[lastIndex].MFI.toFixed(2);
        const prevMFI = mfiData[lastIndex - 1]?.MFI.toFixed(2) || 0;

        // 추가된 MFI 지표들
        const currentMFIChange = mfiData[lastIndex].MFI_Change.toFixed(2);
        const currentMFIVolatility = mfiData[lastIndex].MFI_Volatility.toFixed(2);
        const currentMFI_Z_Score = mfiData[lastIndex].MFI_Z_Score.toFixed(2);
        const currentMFI_MA_Crossover = mfiData[lastIndex].MFI_MA_Crossover;

        // 평균 및 표준편차
        const avgMFI = mfiData.slice(lastIndex - period + 1, lastIndex + 1).reduce((acc, val) => acc + val.MFI, 0) / period;
        const avgMFIVolatility = mfiData.slice(lastIndex - period + 1, lastIndex + 1).reduce((acc, val) => acc + val.MFI_Volatility, 0) / period;

        let result = '';

        result += `<strong>현재 MFI</strong>: ${currentMFI}점<br/>`;
        result += `<strong>MFI 변화량</strong>: ${currentMFIChange}<br/>`;
        result += `<strong>MFI 변동성</strong>: ${currentMFIVolatility}<br/>`;
        result += `<strong>MFI Z-스코어</strong>: ${currentMFI_Z_Score}<br/>`;
        result += `<strong>MFI 이동평균 교차 신호</strong>: ${currentMFI_MA_Crossover === 1 ? '골든 크로스' : '데드 크로스'}<br/><br/>`;

        // MFI 변동성 분석
        if (currentMFIVolatility > avgMFIVolatility) {
            result += `MFI 변동성이 평균보다 높습니다. 이는 시장의 불확실성이 증가했음을 의미합니다.<br/>`;
        } else {
            result += `MFI 변동성이 평균 이하입니다. 시장이 안정적인 상태를 유지하고 있습니다.<br/>`;
        }

        // Z-스코어를 통한 이상치 탐지
        if (Math.abs(currentMFI_Z_Score) > 2) {
            result += `MFI 값이 평균으로부터 2표준편차 이상 떨어져 있습니다. 이는 잠재적인 추세 전환 신호일 수 있습니다.<br/>`;
        }

        // 이동평균 교차 분석
        if (currentMFI_MA_Crossover === 1) {
            result += `MFI 단기 이동평균이 장기 이동평균을 상향 돌파했습니다 (골든 크로스). 상승 추세의 시작을 알릴 수 있습니다.<br/>`;
        } else {
            result += `MFI 단기 이동평균이 장기 이동평균을 하향 돌파했습니다 (데드 크로스). 하락 추세의 시작을 알릴 수 있습니다.<br/>`;
        }

        // 기존의 MFI 수준에 따른 해석 추가
        if (currentMFI > 80) {
            result += `현재 MFI가 80을 초과하여 과매수 구간에 있습니다. 가격 조정이 발생할 수 있으니 주의가 필요합니다.<br/>`;
        } else if (currentMFI < 20) {
            result += `현재 MFI가 20 미만으로 과매도 구간에 있습니다. 반등의 기회가 있을 수 있습니다.<br/>`;
        } else {
            result += `현재 MFI가 중립 구간에 있습니다. 시장의 추세를 주시하세요.<br/>`;
        }

        return result;
    };

    useEffect(() => {
        if (mfiData.length > 0 && stockData.length > 0) {
            const result = analyzeMFITrend(mfiData, stockData);
            setAnalysisResult(result);
        }
    }, [mfiData, stockData]);

    return (
        <div>
            <FetchMFIData stockCode={stockCode} onMFIFetch={setMFIData} />
            <FetchStockDataForCode stockCode={stockCode} onSDFCFetch={setStockData} />
            <h4 className="analysis-text">MFI 흐름 분석(14일)</h4>
            <p dangerouslySetInnerHTML={{ __html: analysisResult }}></p>
        </div>
    );
}

export default MFIAnalysis1;
