import React, { useState, useEffect } from "react";
import FetchADXData from "./FetchADXData";

function ADXAnalysis1({ stockCode }) {
    const [adxData, setADXData] = useState([]);
    const [analysisResult, setAnalysisResult] = useState("");

    // ADX 및 +DI, -DI 값을 기반으로 추세를 분석
    const analyzeADXAndDI = (data) => {
        let lastIndex = data.length - 1;
        if (lastIndex < 1) return "데이터가 충분하지 않습니다.";

        const currentADX = data[lastIndex].ADX.toFixed(2);
        const currentDIPlus = data[lastIndex].DI14Plus.toFixed(2);
        const currentDIMinus = data[lastIndex].DI14Minus.toFixed(2);
        const prevADX = data[lastIndex - 1]?.ADX || 0;

        let result = "";

        // ADX 분석 (추세 강도 분석)
        if (currentADX > 25) {
            result += `추세가 강합니다. (ADX: ${currentADX}) <br /><br />`;
        } else {
            result += `추세가 약합니다. (ADX: ${currentADX}) <br /><br />`;
        }

        // +DI, -DI 비교를 통한 방향성 분석
        if (currentDIPlus > currentDIMinus) {
            result += `상승 추세 (+DI: ${currentDIPlus}, -DI: ${currentDIMinus}) <br />`;
            if (currentADX > 25) {
                result += "강한 상승 추세입니다.";
            } else {
                result += "상승 추세이지만 강하지 않습니다.";
            }
        } else if (currentDIPlus < currentDIMinus) {
            result += `하락 추세 (+DI: ${currentDIPlus}, -DI: ${currentDIMinus}) <br />`;
            if (currentADX > 25) {
                result += "강한 하락 추세입니다.";
            } else {
                result += "하락 추세이지만 강하지 않습니다.";
            }
        } else {
            result += "추세가 명확하지 않습니다. +DI와 -DI가 비슷한 값을 가집니다.";
        }

        // ADX 상승/하락 분석
        if (currentADX > prevADX) {
            result += "<br /><br />추세가 강화되고 있습니다.";
        } else if (currentADX < prevADX) {
            result += "<br /><br />추세가 약해지고 있습니다.";
        }

        return result;
    };

    useEffect(() => {
        if (adxData.length > 0) {
            const result = analyzeADXAndDI(adxData);
            setAnalysisResult(result);
        }
    }, [adxData]);

    return (
        <div>
            <FetchADXData stockCode={stockCode} onADXFetch={setADXData} />
            <h4 className="analysis-text">ADX 및 DI 분석</h4>
            <p dangerouslySetInnerHTML={{__html: analysisResult.replace(/\n/g, '<br />')}}></p>
        </div>
    );
}

export default ADXAnalysis1;
