import React, { useState, useEffect } from 'react';
import FetchSARData from '../ComponentsFetch/FetchParabolicSARData';

function SARAnalysis1({ stockCode }) {
    const [sarData, setSARData] = useState([]);
    const [analysisResult, setAnalysisResult] = useState('');

    const analyzeTrendDirection = (sarData) => {
        if (sarData.length < 2) {
            return '데이터가 충분하지 않습니다.';
        }

        const lastIndex = sarData.length - 1;
        const currentPrice = sarData[lastIndex].close;
        const currentSAR = sarData[lastIndex].Parabolic_SAR;
        const prevPrice = sarData[lastIndex - 1].close;
        const prevSAR = sarData[lastIndex - 1].Parabolic_SAR;

        let result = '<strong>추세 방향 및 반전 지점 식별</strong><br/><br/>';

        // 현재 추세 판단
        if (currentPrice > currentSAR) {
            result += '현재 상승 추세입니다. 가격이 SAR보다 위에 있습니다.<br/>';
        } else if (currentPrice < currentSAR) {
            result += '현재 하락 추세입니다. 가격이 SAR보다 아래에 있습니다.<br/>';
        } else {
            result += '가격이 SAR와 동일합니다. 추세 판단이 어렵습니다.<br/>';
        }

        // 추세 반전 신호 감지
        if ((prevPrice <= prevSAR && currentPrice > currentSAR) || (prevPrice >= prevSAR && currentPrice < currentSAR)) {
            result += '→ 추세 반전 신호가 발생했습니다.<br/>';
        } else {
            result += '→ 추세 반전 신호가 없습니다.<br/>';
        }

        return result;
    };

    useEffect(() => {
        if (sarData.length > 0) {
            const result = analyzeTrendDirection(sarData);
            setAnalysisResult(result);
        }
    }, [sarData]);

    return (
        <div>
            <FetchSARData stockCode={stockCode} onSARFetch={setSARData} />
            <h4>Parabolic SAR 추세 방향 분석</h4>
            <p dangerouslySetInnerHTML={{ __html: analysisResult }}></p>
        </div>
    );
}

export default SARAnalysis1;
