import { useState, useEffect, useRef } from "react";
import { useNavigate } from 'react-router-dom';
import FetchStockList from "../ComponentsFetch/FetchStockOrignal/FetchStockList";
import FetchRealTime from "../ComponentsFetch/FetchStockOrignal/FetchRealTime";
import ChartOfClose from "../ComponentsChart/ChartOfClose";
import Navbar from "../components/Navbar";
import './Home.css'; // 기존 CSS 유지

function Home() {
    const [stockData, setStockData] = useState([]); // 실시간 데이터
    const [stockList, setStockList] = useState([]); // 종목 리스트
    const [filteredData, setFilteredData] = useState([]); // 필터링된 데이터
    const [selectedStock, setSelectedStock] = useState(null);
    const [selectedStockName, setSelectedStockName] = useState("");
    const navigate = useNavigate();
    const [isMenuOpen, setIsMenuOpen] = useState(false);
    const menuRef = useRef(null);

    // 메뉴 클릭 이벤트 처리
    useEffect(() => {
        const outSideClick = (e) => {
            const { target } = e;
            if (isMenuOpen && menuRef.current && !menuRef.current.contains(target)) {
                setIsMenuOpen(false);
            }
        };
        document.body.classList.add('home');
        document.addEventListener("mousedown", outSideClick);
        return () => {
            document.body.classList.remove('home');
            document.removeEventListener("mousedown", outSideClick);
        };
    }, [isMenuOpen]);

    // stockList와 stockData를 조합하여 필터링된 데이터를 생성
    useEffect(() => {
        if (stockList.length > 0 && Object.keys(stockData).length > 0) {
            const formattedData = Object.keys(stockData)
                .map(stockCode => {
                    const matchingStock = stockList.find(stock => stock.code === stockCode);
                    if (matchingStock) {
                        return {
                            ...stockData[stockCode], // stockData에서 실시간 정보를 가져옴
                            stock_code: stockCode, // 종목 코드
                            stock_name: matchingStock.name, // 종목 이름
                        };
                    }
                    return null;
                })
                .filter(data => data !== null) // null 데이터 제외
                .sort((a, b) => b.volume - a.volume); // 거래량(volume) 기준으로 내림차순 정렬

            setFilteredData(formattedData);
        }
    }, [stockList, stockData]);

    const handleStockClick = (stockCode, stockName) => {
        setSelectedStock(stockCode);
        setSelectedStockName(stockName);
    };

    return (
        <>
            <Navbar />

            <div className='home-container'>
                {/* 종목 리스트 불러오기 */}
                <FetchStockList onListFetch={setStockList} />
                {/* 실시간 데이터 불러오기 */}
                <FetchRealTime onDataFetch={setStockData} />

                <div className='table-container'>
                    <table className="table">
                        <thead>
                            <tr>
                                <th>종목명</th>
                                <th>현재가</th>
                                <th>전일대비</th>
                                <th>등락율</th>
                                <th>고가</th>
                                <th>저가</th>
                                <th>시가</th>
                                <th>거래량</th>
                            </tr>
                        </thead>
                        <tbody>
                            {filteredData.map((data, index) => (
                                <tr key={index}>
                                    <td
                                        className="stock_name"
                                        onClick={() => handleStockClick(data.stock_code, data.stock_name)}
                                        style={{
                                            cursor: 'pointer',
                                            color: 'blue',
                                        }}
                                    >
                                        {data.stock_name}
                                    </td>
                                    <td>{data.current_price}우</td>
                                    <td>{data.onday}</td>
                                    <td>{data.ratio}%</td>
                                    <td>{data.high_price}</td>
                                    <td>{data.low_price}</td>
                                    <td>{data.open_price}</td>
                                    <td>{data.volume}</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>

                <div className="chart-container">
                    {selectedStock && (
                        <>
                            <h3 className="chart-container-text">{selectedStockName}</h3>
                            <ChartOfClose stockCode={selectedStock} />
                            <button onClick={() => navigate('/detail', { state: { stockCode: selectedStock, stockName: selectedStockName } })} className="more-button"> 더 보기 </button>
                            <button onClick={() => navigate('/techChart', { state: { stockCode: selectedStock, stockName: selectedStockName } })} className="like-button"> 기술적 차트 </button>
                        </>
                    )}
                </div>
            </div>
        </>
    );
}

export default Home;
