import React,{ useState, useEffect } from "react";
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import FetchStockData from "../components/FetchStockData";

function ChartOfClose(stockCode){
    const [stockData, setStockData] = useState();
    const [filteredData, setFilteredData] = useState([]);

    useEffect(() => {
        const dataForChart = stockData
            .filter(item => item.stock_code === stockCode) // 선택된 종목 코드에 따른 필터링
            .map(item => ({
                date: item.date,
                close: item.close,
            }));

        setFilteredData(dataForChart);
    }, [stockData, stockCode]);



    return (
        <div>
            <FetchStockData onDataFetch={setStockData}/>
            <ResponsiveContainer width="100%" height={400}>
                <LineChart
                    width={500}
                    height={300}
                    data={filteredData}
                    margin={{
                        top: 5, right: 30, left: 20, bottom: 5,
                    }}
                >
                    <CartesianGrid strokeDasharray="3 3"/>
                    <XAxis dataKey="date"/>
                    <YAxis/>
                    <Tooltip/>
                    <Legend/>
                    <Line type="monotone" dataKey="close" stroke="#8884d8" activeDot={{r: 8}}/>
                </LineChart>
            </ResponsiveContainer>
        </div>
    );

}

export default ChartOfClose;
