import React, { useState, useEffect } from "react";
import FetchADXData from "./FetchADXData";

function ADXAnalysis2({ stockCode }) {
    const [diData, setDIData] = useState([]);
    const [analysisResult, setAnalysisResult] = useState("");

    // DI+와 DI- 값 분석
    const analyzeDI = (data) => {
        let lastIndex = data.length - 1;
        if (lastIndex < 1) return "데이터가 충분하지 않습니다.";

        const currentDIPlus = data[lastIndex].DI14Plus.toFixed(2);
        const currentDIMinus = data[lastIndex].DI14Minus.toFixed(2);
        const prevDIPlus = data[lastIndex - 1]?.DI14Plus || 0;
        const prevDIMinus = data[lastIndex - 1]?.DI14Minus || 0;

        let result = "";

        // 1. 상승/하락 추세 분석
        if (currentDIPlus > currentDIMinus) {
            result += `상승 추세 (+DI: ${currentDIPlus}, -DI: ${currentDIMinus}) <br />`;
        } else if (currentDIPlus < currentDIMinus) {
            result += `하락 추세 (+DI: ${currentDIPlus}, -DI: ${currentDIMinus}) <br />`;
        } else {
            result += "추세가 명확하지 않습니다. +DI와 -DI가 비슷한 값을 가집니다.<br />";
        }

        // 2. DI+와 DI-의 수렴/발산 분석
        if (Math.abs(currentDIPlus - currentDIMinus) > Math.abs(prevDIPlus - prevDIMinus)) {
            result += "추세가 강화되고 있습니다. DI+와 DI-의 차이가 커지고 있습니다.<br />";
        } else {
            result += "추세가 약해지고 있습니다. DI+와 DI-의 차이가 줄어들고 있습니다.<br />";
        }

        // 분석 경위 설명
        result += `<br />이 분석은 +DI와 -DI 값을 사용하여 추세 방향을 판단하고, 추세의 강화 또는 약화를 파악하기 위해 수행됩니다. DI+는 상승 추세를 나타내고, DI-는 하락 추세를 나타냅니다.<br /><br />`;

        // 매수/매도 대응 전략 제시
        if (currentDIPlus > currentDIMinus) {
            result += "매수 신호: 상승 추세가 지속되므로 매수 전략을 고려할 수 있습니다.";
        } else if (currentDIPlus < currentDIMinus) {
            result += "매도 신호: 하락 추세가 지속되므로 매도 전략을 고려할 수 있습니다.";
        } else {
            result += "대기: 추세가 명확하지 않으므로 신중한 접근이 필요합니다.";
        }

        return result;
    };

    useEffect(() => {
        if (diData.length > 0) {
            const result = analyzeDI(diData);
            setAnalysisResult(result);
        }
    }, [diData]);

    return (
        <div>
            <FetchADXData stockCode={stockCode} onADXFetch={setDIData} />
            <h4 className="analysis-text">DI+ / DI- 분석</h4>
            <p dangerouslySetInnerHTML={{ __html: analysisResult.replace(/\n/g, '<br />') }}></p>
        </div>
    );
}

export default ADXAnalysis2;
