import React, { useState } from 'react';

function SearchableDropdown({ options, onSelect }) {
    const [filter, setFilter] = useState('');
    const [isVisible, setIsVisible] = useState(false);

    const handleInputChange = (e) => {
        setFilter(e.target.value.toLowerCase());
    };

    const handleOptionClick = (option) => {
        setFilter(option.name);
        onSelect(option.code);
        setIsVisible(false);
    };

    const filteredOptions = options.filter(option =>
        option.name.toLowerCase().startsWith(filter)
    );

    return (
        <div className="searchable-dropdown">
            <input
                type="text"
                value={filter}
                onChange={handleInputChange}
                onFocus={() => setIsVisible(true)}
                placeholder="종목을 검색하세요..."
            />
            {isVisible && (
                <select
                    size={filteredOptions.length > 4 ? 4 : filteredOptions.length}
                    onClick={(e) => handleOptionClick(filteredOptions[e.target.selectedIndex])}
                >
                    {filteredOptions.map(option => (
                        <option key={option.code} value={option.code}>
                            {option.name}
                        </option>
                    ))}
                </select>
            )}
        </div>
    );
}

export default SearchableDropdown;
