import React,{ useState, useEffect } from "react";
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import FetchStockData from "../components/FetchStockData";

function ChartOfClose({ stockCode }) {
    const [stockData, setStockData] = useState([]);
    const [filteredData, setFilteredData] = useState([]);
    const [isLoading, setIsLoading] = useState(true); // 데이터 로딩 상태 관리
    const [error, setError] = useState(null); // 에러 상태 관리

    // 데이터 페칭 함수 정의
    const fetchStockData = async () => {
        try {
            const response = await fetch('http://127.0.0.1:8000/api/stock_data/');
            if (!response.ok) {
                throw new Error('데이터를 가져오는 데 실패했습니다.');
            }
            const data = await response.json();
            setStockData(data);
            setIsLoading(false);
        } catch (err) {
            console.error('Error fetching stock data:', err);
            setError(err.message);
            setIsLoading(false);
        }
    };

    // 컴포넌트 마운트 시 데이터 페칭
    useEffect(() => {
        fetchStockData();
    }, []);

    // stockData 또는 stockCode 변경 시 데이터 필터링
    useEffect(() => {
        if (stockData.length > 0 && stockCode) {
            const dataForChart = stockData
                .filter(item => item.stock_code === stockCode)
                .map(item => ({
                    date: item.date,
                    close: item.close,
                }))
                .sort((a, b) => new Date(a.date) - new Date(b.date)); // 날짜 순으로 정렬

            setFilteredData(dataForChart);
        }
    }, [stockData, stockCode]);

    return (
        <div style={{ width: '100%', height: 400 }}>
            <ResponsiveContainer>
                <LineChart
                    data={filteredData}
                    margin={{ top: 60, right: 30, left: 20, bottom: 0 }}
                >
                    <CartesianGrid strokeDasharray="3 3" />
                    <XAxis dataKey="date" tickCount={5} />
                    <YAxis />
                    <Tooltip />
                    <Legend />
                    <Line
                        type="monotone"
                        dataKey="close"
                        stroke="#8884d8"
                        dot={false}
                    />
                </LineChart>
            </ResponsiveContainer>
        </div>
    );
}

export default ChartOfClose;
