import React, { useState, useEffect } from 'react';
import FetchMACDData from '../ComponentsFetch/FetchMACDData';

function MACDAnalysis4({ stockCode }) {
    const [macdData, setMACDData] = useState([]);
    const [analysisResult, setAnalysisResult] = useState('');

    const analyzeMultiTimeframeMACD = (macdData) => {
        if (macdData.length === 0) {
            return '데이터가 충분하지 않습니다.';
        }

        const lastIndex = macdData.length - 1;
        const currentData = macdData[lastIndex];

        // 단기 MACD 설정 (예: 12,26,9)
        const shortMACD = currentData['MACD_12_26_9'];
        const shortSignal = currentData['Signal_12_26_9'];

        // 장기 MACD 설정 (예: 50,100,9)
        const longMACD = currentData['MACD_50_100_9'];
        const longSignal = currentData['Signal_50_100_9'];

        let result = '';

        // 단기 MACD 분석
        if (shortMACD > shortSignal) {
            result += '단기 MACD: 상승 추세 (MACD Line이 Signal Line 위에 있습니다).<br/>';
        } else {
            result += '단기 MACD: 하락 추세 (MACD Line이 Signal Line 아래에 있습니다).<br/>';
        }

        // 장기 MACD 분석
        if (longMACD > longSignal) {
            result += '장기 MACD: 상승 추세 (MACD Line이 Signal Line 위에 있습니다).<br/>';
        } else {
            result += '장기 MACD: 하락 추세 (MACD Line이 Signal Line 아래에 있습니다).<br/>';
        }

        // 추세 일치 여부 확인
        if ((shortMACD > shortSignal && longMACD > longSignal) || (shortMACD < shortSignal && longMACD < longSignal)) {
            result += '단기 및 장기 추세가 일치합니다. 신뢰도 높은 추세로 판단됩니다.<br/>';
        } else {
            result += '단기 및 장기 추세가 일치하지 않습니다. 신중한 판단이 필요합니다.<br/>';
        }

        return result;
    };

    useEffect(() => {
        if (macdData.length > 0) {
            const result = analyzeMultiTimeframeMACD(macdData);
            setAnalysisResult(result);
        }
    }, [macdData]);

    return (
        <div>
            <FetchMACDData stockCode={stockCode} onMACDFetch={setMACDData} />
            <h4>다중 기간 MACD 분석</h4>
            <p dangerouslySetInnerHTML={{ __html: analysisResult }}></p>
        </div>
    );
}

export default MACDAnalysis4;
