import React, {useState, useEffect} from 'react';
import FetchStockData from "../components/FetchStockData";
import FetchStockList from "../components/FetchStockList";
import '../styles/Navbar.css';
import './Home.css';

function Home() {
    const [stockData, setStockData] = useState([])
    const [stockList, setStockList] = useState([])
    const [filteredData, setFilterData] = useState([])

    useEffect(() => {
        if(stockList.length > 0 && stockData > 0) {
            const latestDataMap = new Map()

            stockData.forEach((data) => {
                const existingData = latestDataMap.get(data.stock_code);
                if (!existingData || new Date(existingData.date) < new Date(data.date)) {
                    latestDataMap.set(data.stock_code, data)
                }
            });

            const filtered = stockList.filter(stock => latestDataMap.has(stock.code))
                                      .map(stock => latestDataMap.get(stock.code));

            setFilteredData(filtered);
        }
    }, []);






    return (
        <>
            <div className='navbar'><h2>ProStock</h2></div>
            <div className='table-container'>
                <table className="table">
                    <thead>
                    <tr>
                        <th>거래소</th>
                        <th>종목명</th>
                        <th>종가</th>
                        <th>전일대비</th>
                        <th>등락률(%)</th>
                        <th>고가</th>
                        <th>저가</th>
                        <th>거래량</th>
                        <th>10일(그래프)</th>
                        <th>30일(그래프)</th>
                    </tr>
                    </thead>
                    <tbody>
                    <tr>
                        <td>KRX</td>
                        <td>삼성전자</td>
                        <td>77,700</td>
                        <td>-600</td>
                        <td>-0.77</td>
                        <td>78,000</td>
                        <td>77,500</td>
                        <td>1,119,293</td>
                        <td>[그래프]</td>
                        <td>[그래프]</td>
                    </tr>
                    {/* 추가 데이터가 있을 경우, 이곳에 추가 */}
                    </tbody>
                </table>
            </div>
        </>
    );
}

export default Home;
