import React, { useState, useEffect } from "react";
import FetchADXData from "./FetchADXData";
import FetchStockDataForCode from "./FetchStockDataForCode";

function ADXAnalysis1({ stockCode }) {
    const [adxData, setADXData] = useState([]);
    const [priceData, setPriceData] = useState([]);
    const [analysisResult, setAnalysisResult] = useState("");

    // ADX 값 분석
    const analyzeADX = (data, prices) => {
        let lastIndex = data.length - 1;
        if (lastIndex < 1) return "데이터가 충분하지 않습니다.";

        const currentADX = data[lastIndex].ADX;
        const prevADX = data[lastIndex - 1]?.ADX || 0;
        const currentPrice = prices[lastIndex].close;
        const prevPrice = prices[lastIndex - 1]?.close || 0;

        let result = "";

        // 1. ADX 추세 강도 분석
        if (currentADX < 20) {
            result += `매우 약한 추세 (ADX: ${currentADX}) <br /><br />`;
        } else if (currentADX >= 20 && currentADX < 25) {
            result += `약한 추세가 형성 중입니다 (ADX: ${currentADX}) <br /><br />`;
        } else if (currentADX >= 25 && currentADX < 30) {
            result += `중간 강도의 추세 (ADX: ${currentADX}) <br /><br />`;
        } else if (currentADX >= 30 && currentADX < 40) {
            result += `강한 추세 (ADX: ${currentADX}) <br /><br />`;
        } else if (currentADX >= 40) {
            result += `매우 강한 추세 (ADX: ${currentADX}) <br /><br />`;
        }

        // 2. ADX 상승/하락에 따른 추세 변화 분석
        if (currentADX > prevADX) {
            result += "추세가 강화되고 있습니다.<br /><br />";
        } else if (currentADX < prevADX) {
            result += "추세가 약해지고 있습니다.<br /><br />";
        }

        // 3. ADX와 가격 변동의 관계 분석
        if (currentPrice > prevPrice && currentADX > prevADX) {
            result += "가격 상승과 함께 추세가 강화되고 있습니다.<br />";
        } else if (currentPrice < prevPrice && currentADX > prevADX) {
            result += "가격 하락과 함께 추세가 강화되고 있습니다.<br />";
        } else if (currentPrice > prevPrice && currentADX < prevADX) {
            result += "가격은 상승 중이나, 추세가 약화되고 있습니다. 주의가 필요합니다.<br />";
        } else if (currentPrice < prevPrice && currentADX < prevADX) {
            result += "가격은 하락 중이나, 추세가 약화되고 있습니다. 반등 가능성이 있습니다.<br />";
        }

        // 분석 경위 설명
        result += `<br />이 분석은 시장의 추세 강도를 파악하기 위해 ADX 값을 사용합니다. ADX 값은 추세의 강도를 나타내며, 값이 25 이상일 때 강한 추세로 간주됩니다. ADX와 가격 변동의 상관관계를 통해 추세의 신뢰성을 판단합니다.<br /><br />`;

        // 매수/매도 대응 전략 제시
        if (currentADX > 25 && currentPrice > prevPrice) {
            result += "매수 신호: 강한 상승 추세가 유지되고 있으므로 매수 전략을 고려할 수 있습니다.";
        } else if (currentADX > 25 && currentPrice < prevPrice) {
            result += "매도 신호: 강한 하락 추세가 유지되고 있으므로 매도 전략을 고려할 수 있습니다.";
        } else {
            result += "대기: 추세가 명확하지 않거나 약화되고 있으므로 신중한 접근이 필요합니다.";
        }

        return result;
    };

    useEffect(() => {
        if (adxData.length > 0 && priceData.length > 0) {
            const result = analyzeADX(adxData, priceData);
            setAnalysisResult(result);
        }
    }, [adxData, priceData]);

    return (
        <div>
            <FetchADXData stockCode={stockCode} onADXFetch={setADXData} />
            <FetchStockDataForCode stockCode={stockCode} onSDFCFetch={setPriceData} />
            <h4 className="analysis-text">ADX 분석</h4>
            <p dangerouslySetInnerHTML={{ __html: analysisResult.replace(/\n/g, '<br />') }}></p>
        </div>
    );
}

export default ADXAnalysis1;
