import React, { useState, useEffect } from 'react';
import FetchMFIData from '../fetchcomponents/FetchMFIData';
import FetchStockDataForCode from '../fetchcomponents/FetchStockDataForCode';

// MFI 변동성 분석 컴포넌트
function MFIAnalysis3({ stockCode }) {
    const [mfiData, setMFIData] = useState([]);
    const [stockData, setStockData] = useState([]);
    const [analysisResult, setAnalysisResult] = useState('');

    // MFI 변동성 분석 함수
    const analyzeMFIVolatility = (mfiData) => {
        const period = 14;  // 분석 기간 (기본값: 14일)
        let lastIndex = mfiData.length - 1;
        if (lastIndex < period) return '데이터가 충분하지 않습니다.';

        const currentVolatility = mfiData[lastIndex].MFI_Volatility?.toFixed(2);
        const prevVolatility = mfiData[lastIndex - 1]?.MFI_Volatility?.toFixed(2);
        const priceRising = stockData[lastIndex].close > stockData[lastIndex - 1]?.close;

        let result = '';

        // 1. 변동성 크기 분석
        if (currentVolatility > 5) {
            result += `현재 변동성은 매우 높은 상태 (${currentVolatility}). 이는 큰 가격 변동 가능성을 나타냅니다. 주의하세요.<br/><br/>`;
        } else if (currentVolatility < 2) {
            result += `현재 변동성은 매우 낮은 상태 (${currentVolatility}). 시장이 안정적입니다.<br/><br/>`;
        } else {
            result += `현재 변동성은 중간 정도 (${currentVolatility})로, 시장은 비교적 안정적이지만 주의가 필요합니다.<br/><br/>`;
        }

        // 2. 변동성 증가/감소 분석
        if (currentVolatility > prevVolatility) {
            result += `MFI 변동성이 증가하고 있습니다. 이는 시장의 변동성이 커질 가능성을 시사하며, 가격 변동이 예상됩니다.<br/>`;
        } else if (currentVolatility < prevVolatility) {
            result += `MFI 변동성이 감소하고 있습니다. 시장이 안정화되고 있는 신호입니다.<br/>`;
        }

        // 3. 가격 상승/하락과의 관계 분석
        if (currentVolatility > 5 && priceRising) {
            result += `가격이 상승하는 동안 변동성이 증가하고 있습니다. 강세 시장이 더욱 강화될 가능성이 있습니다.<br/>`;
        } else if (currentVolatility > 5 && !priceRising) {
            result += `가격이 하락하는 동안 변동성이 증가하고 있습니다. 약세 시장으로의 전환 가능성을 주시하세요.<br/>`;
        }

        return result;
    };

    useEffect(() => {
        if (mfiData.length > 0 && stockData.length > 0) {
            const result = analyzeMFIVolatility(mfiData);
            setAnalysisResult(result);
        }
    }, [mfiData, stockData]);

    return (
        <div>
            <FetchMFIData stockCode={stockCode} onMFIFetch={setMFIData} />
            <FetchStockDataForCode stockCode={stockCode} onSDFCFetch={setStockData} />
            <h4>MFI 변동성 분석</h4>
            <p dangerouslySetInnerHTML={{ __html: analysisResult.replace(/\n/g, '<br />') }}></p>
        </div>
    );
}

export default MFIAnalysis3;
