import React, { useState, useEffect } from 'react';
import FetchStochasticData from "../ComponentsFetch/FetchStochasticData";

function StochasticTotalCalculation({ stockCode, onResultUpdate }) {
    const [stochasticData, setStochasticData] = useState(null);

    useEffect(() => {
        if (stochasticData) {
            let lastDate = stochasticData.length - 1;
            const latestData = stochasticData[lastDate];
            console.log(latestData);
            const recommendation = getRecommendation(latestData.score); // 받아온 점수에 따른 추천

            if (onResultUpdate) {
                onResultUpdate({
                    name: 'Stochastic %K(14, 3, 3)',
                    value: latestData.perK,
                    damm: latestData.score,
                    recommendation: recommendation
                });
            }
        }
    }, [stochasticData]); // stochasticData가 변경될 때마다 실행

    const getRecommendation = (score) => {
        if (score <= 2.0) {
            return '강한 매수';
        } else if (score <= 4.0) {
            return '매수';
        } else if (score <= 6.0) {
            return '보통';
        } else if (score <= 8.0) {
            return '매도';
        } else {
            return '강한 매도';
        }
    };

    return (
        <>
            <FetchStochasticData stockCode={stockCode} onStochasticFetch={setStochasticData} />
        </>
    );
}

export default StochasticTotalCalculation;
