import React, { useState, useEffect } from 'react';
import FetchMFIData from '../ComponentsFetch/FetchMFIData';
import FetchStockDataForCode from '../ComponentsFetch/FetchStockDataForCode';

function MFIAnalysis3({ stockCode }) {
  const [mfiData, setMFIData] = useState([]);
  const [stockData, setStockData] = useState([]);
  const [analysisResult, setAnalysisResult] = useState('');

  // MFI 변동성 분석 함수
  const analyzeMFIVolatility = (mfiData) => {
    const period = 14; // 분석 기간
    const lastIndex = mfiData.length - 1;

    if (lastIndex < period) return '데이터가 충분하지 않습니다.';

    const currentData = mfiData[lastIndex];
    const prevData = mfiData[lastIndex - 1];

    // 현재 및 이전 MFI 변동성
    const currentVolatility = currentData.MFI_Volatility;
    const prevVolatility = prevData.MFI_Volatility;

    // MFI의 평균 대비 현재 값 비교
    const mfiValues = mfiData.slice(lastIndex - period + 1, lastIndex + 1).map((data) => data.MFI);
    const mfiMean = mfiValues.reduce((sum, value) => sum + value, 0) / period;
    const mfiDifference = currentData.MFI - mfiMean;

    // MFI 이동 평균 크로스오버
    const currentMACrossover = currentData.MFI_MA_Crossover;
    const prevMACrossover = prevData.MFI_MA_Crossover;

    // High Volatility Flag
    const isHighVolatility = currentData.High_Volatility;

    // 가격 정보
    const currentPrice = stockData[lastIndex]?.close;
    const prevPrice = stockData[lastIndex - 1]?.close;
    const priceRising = currentPrice > prevPrice;

    let result = '';

    // 1. 변동성 수준 설명 (기술 용어 없이)
    if (isHighVolatility) {
      result += `현재 MFI 변동성이 높습니다 (${currentVolatility.toFixed(2)}). 이는 시장에 큰 움직임이 있을 수 있음을 의미합니다.<br/><br/>`;
    } else {
      result += `현재 MFI 변동성이 낮습니다 (${currentVolatility.toFixed(2)}). 시장이 비교적 안정적인 상태일 수 있습니다.<br/><br/>`;
    }

    // 2. MFI 값이 평균보다 높은지 낮은지 비교
    if (mfiDifference > 10) {
      result += `현재 MFI가 평균보다 높습니다 (${currentData.MFI.toFixed(2)}). 이는 매수세가 강하다는 것을 나타냅니다.<br/><br/>`;
    } else if (mfiDifference < -10) {
      result += `현재 MFI가 평균보다 낮습니다 (${currentData.MFI.toFixed(2)}). 이는 매도세가 강하다는 것을 나타냅니다.<br/><br/>`;
    } else {
      result += `현재 MFI가 평균과 유사합니다 (${currentData.MFI.toFixed(2)}). 시장이 균형 상태에 있을 수 있습니다.<br/><br/>`;
    }

    // 3. 이동 평균 크로스오버 분석 (기술 용어 없이)
    if (prevMACrossover !== currentMACrossover) {
      if (currentMACrossover === 1) {
        result += `MFI 지표의 단기 추세가 상승으로 전환되었습니다. 이는 가격 상승의 신호일 수 있습니다.<br/><br/>`;
      } else {
        result += `MFI 지표의 단기 추세가 하락으로 전환되었습니다. 이는 가격 하락의 신호일 수 있습니다.<br/><br/>`;
      }
    }

    // 4. 가격 움직임과의 관계 분석
    if (priceRising && currentMACrossover === 1) {
      result += `가격이 상승하고 있으며, MFI 지표도 상승 추세를 보이고 있습니다. 이는 강한 매수 신호일 수 있습니다.<br/>`;
    } else if (!priceRising && currentMACrossover === -1) {
      result += `가격이 하락하고 있으며, MFI 지표도 하락 추세를 보이고 있습니다. 이는 매도 신호일 수 있습니다.<br/>`;
    } else {
      result += `가격과 MFI 지표의 움직임이 일치하지 않습니다. 추가적인 분석이 필요합니다.<br/>`;
    }

    // 5. 마무리 분석 또는 설명 추가
    result += `<br/>현재 분석을 종합해보면, 해당 종목은 ${priceRising ? '상승' : '하락'} 추세를 보이고 있으며, MFI 지표도 이를 ${priceRising ? '지지' : '반영'}하고 있습니다. 투자 결정 시 이러한 지표들을 참고하여 신중한 판단이 필요합니다.`;

    return result;
  };

  useEffect(() => {
    if (mfiData.length > 0 && stockData.length > 0) {
      const result = analyzeMFIVolatility(mfiData);
      setAnalysisResult(result);
    }
  }, [mfiData, stockData]);

  return (
    <div>
      <FetchMFIData stockCode={stockCode} onMFIFetch={setMFIData} />
      <FetchStockDataForCode stockCode={stockCode} onSDFCFetch={setStockData} />
      <h4>MFI 변동성 분석</h4>
      <p dangerouslySetInnerHTML={{ __html: analysisResult }}></p>
    </div>
  );
}

export default MFIAnalysis3;
