import React, { useState, useEffect } from "react";
import Highcharts from "highcharts";
import HighchartsReact from "highcharts-react-official";
import FetchStockDataForCode from "./FetchStockDataForCode";
import FetchKeltnerData from "./FetchKeltnerData";

function ChartOfKeltner({ stockCode }) {
    const [stockData, setStockData] = useState([]);
    const [keltnerData, setKeltnerData] = useState([]);
    const [options, setOptions] = useState({});

    useEffect(() => {
        if (stockData.length > 0 && keltnerData.length > 0 && stockCode) {
            const dataForChart = stockData
                .map(item => ({
                    date: new Date(item.date).getTime(), // timestamp로 변경
                    close: item.close,
                }))
                .sort((a, b) => new Date(a.date) - new Date(b.date));

            const upperBand = keltnerData.map(item => [new Date(item.date).getTime(), item.Upper_Band]);
            const middleLine = keltnerData.map(item => [new Date(item.date).getTime(), item.Middle_Line]);
            const lowerBand = keltnerData.map(item => [new Date(item.date).getTime(), item.Lower_Band]);

            // Set chart options
            setOptions({
                chart: {
                    type: 'line',
                    height: 400,
                },
                title: {
                    text: 'Keltner Channel',
                },
                xAxis: {
                    type: 'datetime', // datetime 유형으로 변경
                    labels: {
                        format: '{value:%Y-%m-%d}', // 날짜 형식
                    },
                },
                yAxis: {
                    title: {
                        text: '',
                    },
                },
                series: [
                    {
                        name: 'Close Price',
                        data: dataForChart.map(item => [item.date, item.close]),
                        marker: {
                            enabled: false, // Disable data point markers for close price
                        },
                        color: '#0071e3',
                        lineWidth: 1,
                    },
                    {
                        name: 'Keltner Channel (Area)',
                        type: 'arearange',
                        data: upperBand.map((item, index) => [item[0], lowerBand[index][1], item[1]]),
                        color: 'rgba(135, 206, 250, 0.2)', // 연한 하늘색으로 채우기
                        lineWidth: 0, // 선을 그리지 않음
                        enableMouseTracking: false, // 마우스 오버 비활성화
                    },
                    {
                        name: 'Upper Band',
                        data: upperBand,
                        color: 'skyblue', // 하늘색
                        lineWidth: 1.5,
                        dashStyle: 'solid',
                    },
                    {
                        name: 'Middle Line',
                        data: middleLine,
                        color: 'skyblue', // 하늘색
                        lineWidth: 1.5,
                        dashStyle: 'solid',
                    },
                    {
                        name: 'Lower Band',
                        data: lowerBand,
                        color: 'skyblue', // 하늘색
                        lineWidth: 1.5,
                        dashStyle: 'solid',
                    },
                ],
                legend: {
                    enabled: true, // Show legend to distinguish between lines
                },
                credits: {
                    enabled: false, // Disable the "Highcharts.com" text
                },
                tooltip: {
                    shared: true,
                    crosshairs: true,
                },
            });
        }
    }, [stockData, keltnerData, stockCode]);

    return (
        <div>
            <FetchStockDataForCode stockCode={stockCode} onSDFCFetch={setStockData} />
            <FetchKeltnerData stockCode={stockCode} onKelFetch={setKeltnerData} />
            <HighchartsReact highcharts={Highcharts} options={options} />
        </div>
    );
}

export default ChartOfKeltner;
