import React, { useState, useEffect } from 'react';
import FetchMFIData from './FetchMFIData';
import FetchStockDataForCode from './FetchStockDataForCode';

function MFIAnalysis2({ stockCode }) {
    const [mfiData, setMFIData] = useState([]);
    const [stockData, setStockData] = useState([]);
    const [analysisResult, setAnalysisResult] = useState('');

    // MFI 분석
    const analyzeMFI = (data) => {
        let lastIndex = data.length - 1;
        if (lastIndex < 1) return '데이터가 충분하지 않습니다.';

        const currentMFI = data[lastIndex].MFI.toFixed(2);
        const prevMFI = data[lastIndex - 1]?.MFI || 0;

        let result = '';

        // 1. MFI 점수에 따른 과매수/과매도 분석
        if (currentMFI > 80) {
            result += `과매수 상태 (MFI: ${currentMFI})<br />매도 신호가 가까워질 수 있습니다.<br /><br />`;
        } else if (currentMFI < 20) {
            result += `과매도 상태 (MFI: ${currentMFI})<br />매수 기회를 고려하십시오.<br /><br />`;
        } else {
            result += `중립 상태 (MFI: ${currentMFI})<br />현재 특별한 신호는 없습니다.<br /><br />`;
        }

        // 2. MFI 상승 또는 하락 추세 분석
        if (currentMFI >= 76 && currentMFI < 80) {
            result += `MFI가 80점에 가까워지고 있습니다. <br />과매수 지점 진입 예정으로 추가 대응을 준비하세요.<br /><br />`;
        } else if (currentMFI <= 24 && currentMFI > 20) {
            result += `MFI가 20점에 가까워지고 있습니다. <br />과매도 지점 진입 예정으로 추가 대응을 준비하세요.<br /><br />`;
        }

        // 3. MFI 80점 돌파 및 20점 돌파 시 안내
        if (currentMFI > 80) {
            result += `MFI가 80점을 넘었습니다. 과매수 상태로, 매도 신호에 주의하십시오.<br /><br />`;
        } else if (currentMFI < 20) {
            result += `MFI가 20점을 넘었습니다. 과매도 상태로, 반등 기회를 주의 깊게 살펴보십시오.<br /><br />`;
        }

        // 4. 과매수/과매도 이후 변화 및 추세 변화 가능성
        if (currentMFI > 80 && prevMFI < 80) {
            result += `과매수 구간에 새로 진입했습니다. 시장 과열을 주의하십시오.<br /><br />`;
        } else if (currentMFI < 20 && prevMFI > 20) {
            result += `과매도 구간에 새로 진입했습니다. 반등 가능성을 고려하십시오.<br /><br />`;
        }

        // 5. 현재 임박한 전환 신호 유무
        if (currentMFI > 80) {
            result += `임박한 매도 전환 신호: 과매수 구간입니다. 신중한 매도 전략이 필요합니다.<br />`;
        } else if (currentMFI < 20) {
            result += `임박한 매수 전환 신호: 과매도 구간입니다. 반등 기회를 주시하십시오.<br />`;
        }

        return result;
    };

    useEffect(() => {
        if (mfiData.length > 0) {
            const result = analyzeMFI(mfiData);
            setAnalysisResult(result);
        }
    }, [mfiData]);

    return (
        <div>
            <FetchMFIData stockCode={stockCode} onMFIFetch={setMFIData} />
            <FetchStockDataForCode stockCode={stockCode} onSDFCFetch={setStockData} />
            <h4 className="analysis-text">MFI 분석</h4>
            <p dangerouslySetInnerHTML={{ __html: analysisResult.replace(/\n/g, '<br />') }}></p>
        </div>
    );
}

export default MFIAnalysis2;
