import React, { useState, useEffect } from "react";
import Highcharts from "highcharts";
import HighchartsReact from "highcharts-react-official";
import FetchStockDataForCode from "./FetchStockDataForCode";

function ChartOfClose({ stockCode }) {
    const [stockData, setStockData] = useState([]);
    const [options, setOptions] = useState({});

    useEffect(() => {
        if (stockData.length > 0 && stockCode) {
            const dataForChart = stockData
                .map(item => ({
                    date: new Date(item.date).toLocaleDateString('ko-KR', { month: '2-digit', day: '2-digit' }),
                    close: item.close,
                }))
                .sort((a, b) => new Date(a.date) - new Date(b.date));

            // Set chart options
            setOptions({
                chart: {
                    type: 'line',
                    height: 330,
                    events: {
                        load: function () {
                            // Chart 내부에 텍스트 추가
                            this.renderer.text('Close Price', 100, 40) // 위치와 텍스트 설정
                                .css({
                                    color: '#0071e3', // 텍스트 색상
                                    fontSize: '14px', // 폰트 크기 조정
                                    fontWeight: 'bold', // 폰트 굵게
                                    zIndex: 5,
                                })
                                .add();
                        }
                    }
                },
                title: {
                    text: '',
                },
                accessibility: {
                    enabled: false, // 접근성 모듈 비활성화
                },
                xAxis: {
                    categories: dataForChart.map(item => item.date),
                    labels: {
                        formatter: function () {
                            return this.value; // 날짜 형식 유지 (MM/DD)
                        },
                    },
                },
                yAxis: {
                    title: {
                        text: '',
                    },
                },
                series: [
                    {
                        name: 'Close Price',
                        data: dataForChart.map(item => item.close),
                        marker: {
                            enabled: false, // 데이터 포인트 마커 비활성화
                        },
                        color: '#0071e3',
                    },
                ],
                legend: {
                    enabled: false, // 하단 범례 비활성화
                },
                credits: {
                    enabled: false, // "Highcharts.com" 텍스트 비활성화
                },
                tooltip: {
                    shared: true,
                    crosshairs: true,
                },
            });
        }
    }, [stockData, stockCode]);

    if (!stockCode) return null;

    return (
        <div>
            <FetchStockDataForCode stockCode={stockCode} onSDFCFetch={setStockData} />
            <HighchartsReact highcharts={Highcharts} options={options} />
        </div>
    );
}

export default ChartOfClose;
