import React, { useState, useEffect } from 'react';
import FetchMFIData from './FetchMFIData';
import FetchStockDataForCode from './FetchStockDataForCode';

function MFIAnalysis1({ stockCode }) {
    const [mfiData, setMFIData] = useState([]);
    const [stockData, setStockData] = useState([]);
    const [analysisResult, setAnalysisResult] = useState('');

    // MFI 점수 및 기타 분석
    const analyzeMFI = (data) => {
        let lastIndex = data.length - 1;
        if (lastIndex < 1) return '데이터가 충분하지 않습니다.';

        const currentMFI = data[lastIndex].MFI.toFixed(2);
        const prevMFI = data[lastIndex - 1]?.MFI || 0;

        let result = '';

        // 1. MFI 점수 (MFI Value) 과매수/과매도 분석
        if (currentMFI > 80) {
            result += `과매수 영역에 있습니다. (MFI: ${currentMFI}) <br /> 추가 상승 가능성은 낮으며, 주의가 필요합니다. <br /><br />`;
        } else if (currentMFI < 20) {
            result += `과매도 영역에 있습니다. (MFI: ${currentMFI}) <br /> 반등 가능성이 높습니다. 매수 기회를 고려할 수 있습니다. <br /><br />`;
        } else {
            result += `중립 영역에 있습니다. (MFI: ${currentMFI}) <br /> 명확한 매수/매도 신호가 아직 나타나지 않았습니다. <br /><br />`;
        }

        // 2. 과매수/과매도 이후 변화 분석
        if (currentMFI > 80 && prevMFI < 80) {
            result += '과매수 구간에 새로 진입했습니다. 시장이 과열될 가능성이 있으므로 매도 기회를 고려하십시오. <br />';
        } else if (currentMFI < 20 && prevMFI > 20) {
            result += '과매도 구간에 새로 진입했습니다. 이는 반등할 가능성이 높으며, 매수 기회를 고려할 수 있습니다. <br />';
        } else if (currentMFI <= 80 && prevMFI > 80) {
            result += '과매수 구간에서 빠져나왔습니다. 상승 추세가 약화될 수 있습니다. <br />';
        } else if (currentMFI >= 20 && prevMFI < 20) {
            result += '과매도 구간에서 빠져나왔습니다. 반등 신호가 약해질 수 있습니다. <br />';
        } else {
            result += '과매수/과매도 이후 변화가 아직 발생하지 않았습니다. <br />';
        }

        // 3. 임박한 전환 신호 분석
        if (currentMFI > 80) {
            result += '임박한 매도 전환 신호: 과매수 구간에 있습니다. 상승세가 둔화될 수 있으니 주의하십시오. <br />';
        } else if (currentMFI < 20) {
            result += '임박한 매수 전환 신호: 과매도 구간에 있습니다. 가격이 반등할 가능성이 높아집니다. <br />';
        } else {
            result += '현재 임박한 전환 신호는 발생하지 않았습니다. <br />지속적인 모니터링이 필요하며, 신호가 발생할 경우 추가 안내가 제공될 수 있습니다. <br />';
        }

        return result;
    };

    useEffect(() => {
        if (mfiData.length > 0) {
            const result = analyzeMFI(mfiData);
            setAnalysisResult(result);
        }
    }, [mfiData]);

    return (
        <div>
            <FetchMFIData stockCode={stockCode} onMFIFetch={setMFIData} />
            <FetchStockDataForCode stockCode={stockCode} onSDFCFetch={setStockData} />
            <h4 className="analysis-text">MFI 분석</h4>
            <p dangerouslySetInnerHTML={{ __html: analysisResult.replace(/\n/g, '<br />') }}></p>
        </div>
    );
}

export default MFIAnalysis1;
