import React, { useState, useEffect } from 'react';
import FetchTop5Data from '../ComponentsFetch/FetchStockOrignal/FetchTop5Data'
import FetchStockList from '../ComponentsFetch/FetchStockOrignal/FetchStockList'; // FetchStockList 컴포넌트
import FetchStockDataForCode from '../ComponentsFetch/FetchStockOrignal/FetchStockDataForCode'; // FetchStockDataForCode 컴포넌트

function TechChart() {
    const [top5Stocks, setTop5Stocks] = useState([]); // 상위 5개의 종목 데이터를 저장할 상태
    const [stockList, setStockList] = useState([]); // 전체 종목 목록 저장
    const [selectedStock, setSelectedStock] = useState(null); // 선택된 종목 코드
    const [stockDetails, setStockDetails] = useState(null); // 선택된 종목의 세부 정보 저장
    

    // FetchTop5Data의 콜백 핸들러
    const handleTopFetch = (data) => {
        setTop5Stocks(data); // 상위 5개의 종목 데이터를 상태에 저장
    };

    // FetchStockList의 콜백 핸들러
    const handleListFetch = (data) => {
        setStockList(data); // 전체 종목 목록을 상태에 저장
    };

    // 특정 종목을 선택했을 때 해당 종목의 세부 데이터를 가져오는 함수
    const handleStockClick = (stockCode) => {
        setSelectedStock(stockCode); // 클릭한 종목의 코드를 저장
    };

    // FetchStockDataForCode의 콜백 핸들러
    const handleStockDataFetch = (data) => {
        setStockDetails(data); // 선택된 종목의 세부 데이터를 저장
    };

    return (
        <div>
            <FetchTop5Data onTopFetch={handleTopFetch} />
            <FetchStockList onListFetch={handleListFetch} />

            {/* 상위 5개의 종목을 출력 */}
            <h2>Top 5 Stocks</h2>
            <ul>
                {top5Stocks.map((stock, index) => (
                    <li key={index} onClick={() => handleStockClick(stock.stock_code)}>
                        {stock.stock_name} - 점수: {stock.score.toFixed(2)}
                    </li>
                ))}
            </ul>

            {/* 특정 종목을 클릭하면 해당 종목의 세부 정보를 가져옴 */}
            {selectedStock && (
                <>
                    <h3>Selected Stock Details: {selectedStock}</h3>
                    <FetchStockDataForCode stockCode={selectedStock} onSDFCFetch={handleStockDataFetch} />
                </>
            )}

            {/* 선택된 종목의 세부 정보를 화면에 출력 */}
            {stockDetails && (
                <div>
                    <h4>Stock Details:</h4>
                    <p>Close Price: {stockDetails.close}</p>
                    <p>High Price: {stockDetails.high}</p>
                    <p>Low Price: {stockDetails.low}</p>
                    <p>Volume: {stockDetails.volume}</p>
                </div>
            )}
        </div>
    );
}

export default TechChart;
