import React, { useState, useEffect } from "react";
import FetchParabolicSARData from "./FetchParabolicSARData";
import FetchStockDataForCode from "./FetchStockDataForCode";
import Tooltip from '@mui/material/Tooltip';

function SARAnalysis1({ stockCode }) {
    const [sarData, setSARData] = useState([]);
    const [priceData, setPriceData] = useState([]);
    const [analysisResult, setAnalysisResult] = useState("");

    // Parabolic SAR 값 분석
    const analyzeParabolicSAR = (data, prices) => {
        let lastIndex = data.length - 1;
        if (lastIndex < 1) return "데이터가 충분하지 않습니다.";

        const currentSAR = data[lastIndex].Parabolic_SAR;
        const prevSAR = data[lastIndex - 1]?.Parabolic_SAR || 0;
        const currentPrice = prices[lastIndex].close;
        const prevPrice = prices[lastIndex - 1]?.close || 0;
        const currentDate = prices[lastIndex].date;
        const prevDate = prices[lastIndex - 1]?.date || "알 수 없음";

        let result = "";

        // 1. Parabolic SAR 추세 강도 분석
        if (currentPrice > currentSAR) {
            result += `상승 추세가 유지되고 있습니다. (SAR: ${currentSAR}.toFixed(2), Close: ${currentPrice})<br /><br />`;
        } else if (currentPrice < currentSAR) {
            result += `하락 추세가 유지되고 있습니다. (SAR: ${currentSAR}.toFixed(2), Close: ${currentPrice})<br /><br />`;
        }

        // 2. 추세 전환 가능성 분석
        if (currentPrice > currentSAR && prevPrice < prevSAR) {
            result += `최근 (${prevDate}) 종가의 가격이 SAR을 돌파하여 상승 추세로 전환되었습니다. <br>현재 가격 흐름은 (${currentDate}) 상승 추세를 유지하고 있어 가격 상승의 여력이 있습니다.<br />`;
        } else if (currentPrice < currentSAR && prevPrice > prevSAR) {
            result += `최근 (${prevDate}) 종가의 가격이 SAR 아래로 내려가 하락 추세로 전환되었습니다. <br>현재 가격 흐름은 (${currentDate}) 하락 추세를 유지하고 있어 추가 하락 가능성이 큽니다.<br />`;
        }

        // 3. Parabolic SAR와 가격 변동의 관계 분석 (주의 신호)
        if (currentPrice > currentSAR && prevPrice > prevSAR) {
            result += "<br />가격이 계속 상승 중이며, 추세가 유지되고 있습니다.<br /><br />";
        } else if (currentPrice < currentSAR && prevPrice < prevSAR) {
            result += "<br />가격이 계속 하락 중이며, 추세가 유지되고 있습니다.<br /><br />";
        } else if (currentPrice > currentSAR && prevPrice < prevSAR) {
            result += "<br />추세 반전 가능성이 있으니 매수 전략을 고려할 수 있습니다.<br /><br />";
        } else if (currentPrice < currentSAR && prevPrice > prevSAR) {
            result += "<br />추세 반전 가능성이 있으니 매도 전략을 고려할 수 있습니다.<br /><br />";
        }

        // 4. 매수/매도 대응 전략
        if (currentPrice > currentSAR) {
            result += "매수 신호: 상승 추세가 유지되고 있으므로 매수 전략을 고려할 수 있습니다.<br />";
        } else if (currentPrice < currentSAR) {
            result += "매도 신호: 하락 추세가 유지되고 있으므로 매도 전략을 고려할 수 있습니다.<br />";
        } else {
            result += "대기: 추세가 명확하지 않으므로 신중한 접근이 필요합니다.<br />";
        }

        return result;
    };

    useEffect(() => {
        if (sarData.length > 0 && priceData.length > 0) {
            const result = analyzeParabolicSAR(sarData, priceData);
            setAnalysisResult(result);
        }
    }, [sarData, priceData]);

    return (
        <div>
            <FetchParabolicSARData stockCode={stockCode} onSARFetch={setSARData} />
            <FetchStockDataForCode stockCode={stockCode} onSDFCFetch={setPriceData} />
            <Tooltip title="이 분석은 Parabolic SAR 값을 통해 추세 전환과 가격 상승 여력을 분석합니다. 가격이 SAR을 넘어서면 상승 추세로의 전환 가능성이 있으며, 이를 바탕으로 매수/매도 전략을 세울 수 있습니다.">
                <h4 className="analysis-text">Parabolic SAR 분석</h4>
            </Tooltip>
            <p dangerouslySetInnerHTML={{ __html: analysisResult.replace(/\n/g, '<br />') }}></p>
        </div>
    );
}

export default SARAnalysis1;
