import React, { useState, useEffect } from "react";
import FetchMACDData from "./FetchMACDData";
import FetchStockDataForCode from "./FetchStockDataForCode";

function MACDAnalysis1({ stockCode }) {
    const [macdData, setMACDData] = useState([]);
    const [priceData, setPriceData] = useState([]);
    const [analysisResult, setAnalysisResult] = useState("");

    const analyzeMACDGap = (data, prices) => {
        let lastIndex = data.length - 1;
        if (lastIndex < 1) return "데이터가 충분하지 않습니다.";

        const currentMACD = data[lastIndex].MACD;
        const currentSignal = data[lastIndex].Signal;
        const prevMACD = data[lastIndex - 1].MACD;
        const prevSignal = data[lastIndex - 1].Signal;
        const currentPrice = prices[lastIndex].close;
        const prevPrice = prices[lastIndex - 1].close;

        const currentGap = currentMACD - currentSignal;
        const prevGap = prevMACD - prevSignal;

        if (currentGap > 0 && prevGap <= 0) {
            return currentPrice > prevPrice
                ? "강한 매수 신호: MACD Line이 Signal Line을 아래에서 위로 교차했습니다. 가격도 상승 중입니다."
                : "주의: MACD Line이 Signal Line을 아래에서 위로 교차했지만 가격이 하락 중입니다. 신중한 접근이 필요합니다.";
        } else if (currentGap < 0 && prevGap >= 0) {
            return currentPrice < prevPrice
                ? "강한 매도 신호: MACD Line이 Signal Line을 위에서 아래로 교차했습니다. 가격도 하락 중입니다."
                : "주의: MACD Line이 Signal Line을 위에서 아래로 교차했지만 가격이 상승 중입니다. 신중한 접근이 필요합니다.";
        } else if (Math.abs(currentGap) < Math.abs(prevGap)) {
            return `약한 ${currentGap > 0 ? "매수" : "매도"} 신호: MACD Line과 Signal Line의 격차가 줄어들고 있습니다. \n 다른 지표를 함께 검토하십시오.`;
        } else if (Math.abs(currentGap) > Math.abs(prevGap)) {
            if (currentMACD > currentSignal && currentPrice > prevPrice) {
                return "좋은 추세 강화 신호: MACD Line이 Signal Line 위에 있고, 격차가 벌어지며 가격도 상승 중입니다.";
            } else if (currentMACD < currentSignal && currentPrice < prevPrice) {
                return "좋지 않은 추세 강화 신호: MACD Line이 Signal Line 아래에 있고, 격차가 벌어지며 가격도 하락 중입니다.";
            } else {
                return `추세 강화 신호: MACD Line과 Signal Line의 격차가 벌어지고 있습니다. 하지만 가격 움직임이 일치하지 않습니다. 신중히 검토하십시오.`;
            }
        } else {
            return "변화 없음: MACD Line과 Signal Line의 격차에 큰 변화가 없습니다.";
        }
    };

    useEffect(() => {
        if (macdData.length > 0 && priceData.length > 0) {
            const result = analyzeMACDGap(macdData, priceData);
            setAnalysisResult(result);
        }
    }, [macdData, priceData]);

    return (
        <div>
            <FetchMACDData stockCode={stockCode} onMACDFetch={setMACDData} />
            <FetchStockDataForCode stockCode={stockCode} onSDFCFetch={setPriceData} />
            <h4 className="analysis-text">MACD Crossovers 분석</h4>
            <p>{analysisResult}</p>
        </div>
    );
}

export default MACDAnalysis1;
