import React, { useState, useEffect } from "react";

function findBullishDivergence(stockData, rsiData) {
    const sortedStockData = [...stockData].sort((a, b) => new Date(a.date) - new Date(b.date));

    let divergences = [];
    let firstLow = null;

    for (let i = 1; i < sortedStockData.length - 1; i++) {
        if (sortedStockData[i - 1].close > sortedStockData[i].close && sortedStockData[i].close < sortedStockData[i + 1].close) {
            if (!firstLow) {
                firstLow = { date: sortedStockData[i].date, close: sortedStockData[i].close };
            } else {
                if (sortedStockData[i].close < firstLow.close) {
                    const firstLowRsi = rsiData.find(rsi => rsi.date === firstLow.date);
                    const secondLowRsi = rsiData.find(rsi => rsi.date === sortedStockData[i].date);

                    if (firstLowRsi && secondLowRsi && secondLowRsi.RSI > firstLowRsi.RSI) {
                        divergences.push({
                            firstLow: firstLow,
                            secondLow: { date: sortedStockData[i].date, close: sortedStockData[i].close },
                        });
                    }

                    // 새로운 저점을 첫 번째 저점으로 갱신
                    firstLow = { date: sortedStockData[i].date, close: sortedStockData[i].close };
                }
            }
        }
    }

    return divergences;
}

function StockAnalysis({ stockData, rsiData }) {
    const [divergences, setDivergences] = useState([]);

    useEffect(() => {
        if (stockData.length > 0 && rsiData.length > 0) {
            const detectedDivergences = findBullishDivergence(stockData, rsiData);
            setDivergences(detectedDivergences);
        }
    }, [stockData, rsiData]);

    return (
        <div>
            <h3>Detected Bullish Divergences</h3>
            <ul>
                {divergences.map((div, index) => (
                    <li key={index}>
                        첫 번째 저점: {div.firstLow.date} (종가: {div.firstLow.close}), 두 번째 저점: {div.secondLow.date} (종가: {div.secondLow.close})
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default StockAnalysis;
