import React, { useState, useEffect } from 'react';
import FetchMACDData from '../ComponentsFetch/FetchMACDData';

function MACDAnalysis1({ stockCode }) {
    const [macdData, setMACDData] = useState([]);
    const [analysisResult, setAnalysisResult] = useState('');

    const analyzeMACDCrossovers = (macdData, period = 14) => {
        if (macdData.length < period) {
            return '데이터가 충분하지 않습니다.';
        }

        const lastIndex = macdData.length - 1;
        const currentMACD = macdData[lastIndex].MACD_Line;
        const currentSignal = macdData[lastIndex].Signal_Line;
        const currentHistogram = macdData[lastIndex].Histogram;
        const prevMACD = macdData[lastIndex - 1].MACD_Line;
        const prevSignal = macdData[lastIndex - 1].Signal_Line;
        const prevHistogram = macdData[lastIndex - 1].Histogram;  // 이전 날 Histogram 값 추가

        let result = `<strong>현재 MACD</strong><br/>`;
        result += `ㆍMACD: ${currentMACD.toFixed(2)}<br/>`;
        result += `ㆍSignal: ${currentSignal.toFixed(2)}<br/>`;
        result += `ㆍHistogram: ${currentHistogram.toFixed(2)}<br/><br/>`;

        // 1. MACD Line과 Signal Line의 크로스 분석
        if (currentHistogram > 0) {
            if (prevMACD < prevSignal && currentMACD > currentSignal) {
                result += '강한 매수 신호: MACD Line이 Signal Line을 아래에서 위로 교차했습니다 (골든 크로스). 히스토그램이 양수로 상승 추세가 강화되고 있습니다.<br/>';
            } else if (prevMACD > prevSignal && currentMACD < currentSignal) {
                result += '강한 매도 신호: MACD Line이 Signal Line을 위에서 아래로 교차했습니다 (데드 크로스). 히스토그램이 양수이지만 매도 신호에 주의해야 합니다.<br/>';
            } else {
                result += '현재 MACD와 Signal Line 사이에 특별한 교차 신호가 없습니다. 히스토그램이 양수로 모멘텀이 강해지고 있습니다.<br/><br/>';
            }
        } else {
            if (prevMACD < prevSignal && currentMACD > currentSignal) {
                result += '매수 신호: MACD Line이 Signal Line을 아래에서 위로 교차했지만, 히스토그램이 음수입니다. 모멘텀이 약해질 수 있으니 추가 주의가 필요합니다.<br/>';
            } else if (prevMACD > prevSignal && currentMACD < currentSignal) {
                result += '매도 신호: MACD Line이 Signal Line을 위에서 아래로 교차했습니다 (데드 크로스). 히스토그램이 음수로 하락 추세가 강화되고 있습니다.<br/>';
            } else {
                result += '현재 MACD와 Signal Line 사이에 특별한 교차 신호가 없습니다. 히스토그램이 음수로 모멘텀이 약화되고 있습니다.<br/><br/>';
            }
        }
        

        // 3. 추가 분석: 이후 히스토그램의 최근 14일간 트렌드 분석
        const histogramTrend = macdData.slice(lastIndex - 5, lastIndex + 1).map(day => day.Histogram);
        const histogramTrendDirection14 = histogramTrend[histogramTrend.length - 1] - histogramTrend[0];

        if (histogramTrendDirection14 > 0) {
            result += '추가 분석: 히스토그램이 최근 5일간 증가하고 있어 모멘텀이 강해지고 있습니다. 상승 추세가 지속될 가능성이 있습니다.<br/>';
        } else if (histogramTrendDirection14 < 0) {
            result += '추가 분석: 히스토그램이 최근 5일간 감소하고 있어 모멘텀이 약화되고 있습니다. 하락 추세로 전환될 수 있습니다.<br/>';
        }

        return result;
    };

    useEffect(() => {
        if (macdData.length > 0) {
            const result = analyzeMACDCrossovers(macdData);
            setAnalysisResult(result);
        }
    }, [macdData]);

    return (
        <div>
            <FetchMACDData stockCode={stockCode} onMACDFetch={setMACDData} />
            <h4>MACD 크로스오버 및 히스토그램 추세 분석</h4>
            <p dangerouslySetInnerHTML={{ __html: analysisResult }}></p>
        </div>
    );
}

export default MACDAnalysis1;
