import React, { useState, useEffect } from "react";
import Highcharts from 'highcharts';
import HighchartsReact from 'highcharts-react-official';

function ChartOfClose({ stockCode }) {
    const [stockData, setStockData] = useState([]);
    const [filteredData, setFilteredData] = useState([]);
    const [isLoading, setIsLoading] = useState(true); // 데이터 로딩 상태 관리
    const [error, setError] = useState(null); // 에러 상태 관리

    const fetchStockData = async () => {
        try {
            const response = await fetch('http://127.0.0.1:8000/api/stock_data/');
            if (!response.ok) {
                throw new Error('데이터를 가져오는 데 실패했습니다.');
            }
            const data = await response.json();
            setStockData(data);
            setIsLoading(false);
        } catch (err) {
            console.error('Error fetching stock data:', err);
            setError(err.message);
            setIsLoading(false);
        }
    };

    useEffect(() => {
        fetchStockData();
    }, []);

    // stockData 또는 stockCode 변경 시 데이터 필터링
    useEffect(() => {
        if (stockData.length > 0 && stockCode) {
            const dataForChart = stockData
                .filter(item => item.stock_code === stockCode)
                .map(item => ({
                    date: item.date,
                    close: item.close,
                }))
                .sort((a, b) => new Date(a.date) - new Date(b.date)); // 날짜 순으로 정렬

            console.log('Filtered Data for Chart:', dataForChart);
            setFilteredData(dataForChart);
        }
    }, [stockData, stockCode]);

    if (isLoading) {
        return <div>데이터를 불러오는 중...</div>;
    }

    if (error) {
        return <div>에러 발생: {error}</div>;
    }

    if (filteredData.length === 0) {
        return <div>선택한 종목의 데이터가 없습니다.</div>;
    }

    const options = {
        title: {
            text: 'Stock Close Price'
        },
        xAxis: {
            categories: filteredData.map(item => item.date),
        },
        series: [
            {
                name: 'Close Price',
                data: filteredData.map(item => item.close),
            }
        ],
        chart: {
            type: 'line',
            height: 300,

        },
        legend: {
            enabled: true,
        },
        tooltip: {
            shared: true,
            crosshairs: true
        },
        plotOptions: {
        line: {
            marker: {
                enabled: false  // 모든 데이터 포인트의 마커를 비활성화
            }
        }
    }
    };

    return (
        <div>
            <HighchartsReact highcharts={Highcharts} options={options} />
        </div>
    );
}

export default ChartOfClose;
