import React from 'react';
import GaugeChartOfOscillators from '../ComponentsChart/GaugeChartOfOscillators';
import GaugeChartOfMovingAverages from '../ComponentsChart/GaugeChartOfMovingAverages';
import FinalChart from '../ComponentsChart/FinalChart';
import "../pages/Detail.css"

function ChartLayout({ stockCode }) {
    return (
        <div className="chart-container">
            {/* 왼쪽에 Oscillators 차트 */}
            <div className="oscillator-chart">
                <GaugeChartOfOscillators stockCode={stockCode} />
            </div>

            {/* 중앙 상단에 FinalChart */}
            <div className="final-chart">
                <FinalChart stockCode={stockCode} />
            </div>

            {/* 오른쪽에 Moving Averages 차트 */}
            <div className="moving-average-chart">
                <GaugeChartOfMovingAverages stockCode={stockCode} />
            </div>
        </div>
    );
}

const styles = {
    container: {
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',  // 중앙 정렬
        width: '100%',
        height: 'auto',
        margin: '0 auto',
    },
    finalChart: {
        width: '60%', // 중앙 차트 너비
        marginBottom: '40px',  // 아래 차트들과의 간격
    },
    bottomCharts: {
        display: 'flex',
        justifyContent: 'space-between',
        width: '80%',  // 좌우 차트들 사이의 공간 확보
    },
    oscillatorChart: {
        width: '45%',  // 왼쪽 차트 너비
    },
    movingAverageChart: {
        width: '45%',  // 오른쪽 차트 너비
    },
};

export default ChartLayout;
