import React, { useState, useEffect } from 'react';
import FetchSARData from '../ComponentsFetch/FetchParabolicSARData';

function SARAnalysis2({ stockCode }) {
    const [sarData, setSARData] = useState([]);
    const [analysisResult, setAnalysisResult] = useState('');

    const analyzeTrendPersistence = (sarData, period = 5) => {
        if (sarData.length < period) {
            return '데이터가 충분하지 않습니다.';
        }

        let result = '<strong>추세 지속성 분석</strong><br/><br/>';

        const recentData = sarData.slice(-period);
        let upTrendCount = 0;
        let downTrendCount = 0;

        recentData.forEach((data) => {
            if (data.close > data.Parabolic_SAR) {
                upTrendCount++;
            } else if (data.close < data.Parabolic_SAR) {
                downTrendCount++;
            }
        });

        if (upTrendCount === period) {
            result += `지난 ${period}일 동안 가격이 SAR보다 위에 있어 강한 상승 추세가 지속되고 있습니다.<br/>`;
        } else if (downTrendCount === period) {
            result += `지난 ${period}일 동안 가격이 SAR보다 아래에 있어 강한 하락 추세가 지속되고 있습니다.<br/>`;
        } else {
            result += `지난 ${period}일 동안 추세가 명확하지 않습니다. 변동성이 높을 수 있습니다.<br/>`;
        }

        return result;
    };

    useEffect(() => {
        if (sarData.length > 0) {
            const result = analyzeTrendPersistence(sarData);
            setAnalysisResult(result);
        }
    }, [sarData]);

    return (
        <div>
            <FetchSARData stockCode={stockCode} onSARFetch={setSARData} />
            <h4>Parabolic SAR 추세 지속성 분석</h4>
            <p dangerouslySetInnerHTML={{ __html: analysisResult }}></p>
        </div>
    );
}

export default SARAnalysis2;
