import React, { useEffect, useState } from "react";
import FetchMACDData from "./FetchMACDData";
import FetchStockDataForCode from "./FetchStockDataForCode";

function MACDAnalysis2({ stockCode }) {
    const [macdData, setMACDData] = useState([]);
    const [priceData, setPriceData] = useState([]);
    const [analysisText, setAnalysisText] = useState("");

    useEffect(() => {
        if (macdData.length > 0 && priceData.length > 0) {
            const analysis = performDivergenceAnalysis();
            setAnalysisText(analysis);
        }
    }, [macdData, priceData]);

    const performDivergenceAnalysis = () => {
        const period = 10;

        // 상승 다이버전스 분석 (저점 기준)
        const firstLow = findLowPoint(priceData, period, priceData.length - 1);
        const secondLow = firstLow ? findLowPoint(priceData, period, priceData.length - 11) : null;

        // 하락 다이버전스 분석 (고점 기준)
        const firstHigh = findHighPoint(priceData, period, priceData.length - 1);
        const secondHigh = firstHigh ? findHighPoint(priceData, period, priceData.length - 11) : null;

        let divergenceResult = '';

        // 상승 다이버전스 분석
        if (firstLow && secondLow) {
            const firstMACDLow = macdData[firstLow.index].MACD;
            const secondMACDLow = macdData[secondLow.index].MACD;

            if (firstLow.price < secondLow.price && firstMACDLow > secondMACDLow) {
                divergenceResult += `상승 다이버전스 발생: 첫 번째 저점(${secondLow.date}, ${secondLow.price}원) 
                                      두 번째 저점(${firstLow.date}, ${firstLow.price}원) <br />
                                      이는 강력한 매수 신호일 수 있습니다.<br /><br />`;
            }
        }

        // 하락 다이버전스 분석
        if (firstHigh && secondHigh) {
            const firstMACDHigh = macdData[firstHigh.index].MACD;
            const secondMACDHigh = macdData[secondHigh.index].MACD;

            if (firstHigh.price > secondHigh.price && firstMACDHigh < secondMACDHigh) {
                divergenceResult += `하락 다이버전스 발생: 첫 번째 고점(${secondHigh.date}, ${secondHigh.price}원) 
                                      두 번째 고점(${firstHigh.date}, ${firstHigh.price}원) <br />
                                      이는 강력한 매도 신호일 수 있습니다.`;
            }
        }

        if (!divergenceResult) {
            divergenceResult = `다이버전스가 발생하지 않았습니다. 현재의 흐름은 안정적일 수 있습니다.`;
        }

        return divergenceResult;
    };

    // 저점 찾기 로직
    const findLowPoint = (data, period, startIndex) => {
        let lowest = { price: Infinity, index: -1, date: "" };

        let currentPeriodEnd = startIndex;
        let currentPeriodStart = Math.max(startIndex - period + 1, 0);

        for (let i = currentPeriodEnd; i >= currentPeriodStart && i >= 2; i--) {
            const a = data[i - 2];
            const b = data[i - 1];
            const c = data[i];

            if ((a.close > b.close && b.close < c.close) || (a.close >= b.close && b.close <= c.close)) {
                if (b.close < lowest.price) {
                    lowest = { price: b.close, index: i - 1, date: b.date };
                }
            }
        }

        return lowest.index !== -1 ? lowest : null;
    };

    // 고점 찾기 로직
    const findHighPoint = (data, period, startIndex) => {
        let highest = { price: -Infinity, index: -1, date: "" };

        let currentPeriodEnd = startIndex;
        let currentPeriodStart = Math.max(startIndex - period + 1, 0);

        for (let i = currentPeriodEnd; i >= currentPeriodStart && i >= 2; i--) {
            const a = data[i - 2];
            const b = data[i - 1];
            const c = data[i];

            if ((a.close < b.close && b.close > c.close) || (a.close <= b.close && b.close >= c.close)) {
                if (b.close > highest.price) {
                    highest = { price: b.close, index: i - 1, date: b.date };
                }
            }
        }

        return highest.index !== -1 ? highest : null;
    };

    return (
        <div>
            <FetchMACDData stockCode={stockCode} onMACDFetch={setMACDData}/>
            <FetchStockDataForCode stockCode={stockCode} onSDFCFetch={setPriceData}/>
            <h4 className="analysis-text">Divergence 분석</h4>
            <p dangerouslySetInnerHTML={{ __html: analysisText.replace(/\n/g, '<br />') }}></p>
        </div>
    );
}

export default MACDAnalysis2;
