import React, { useState, useEffect } from "react";
import Select from "react-select";
import { Link } from "react-router-dom";
import { useLocation } from 'react-router-dom';
import FetchLatestStockData from "../components/FetchLatestStockData";
import FetchStockList from "../components/FetchStockList";
import ChartOfClose from "../components/ChartOfClose";
import ChartOfRSI from "../components/ChartOfRSI";
import RSIAnalysis1 from "../components/RSIAnalysis1";
import RSIAnalysis2 from "../components/RSIAnalysis2";
import RSIAnalysis3 from "../components/RSIAnalysis3"
import RSIAnalysis4 from "../components/RSIAnalysis4"
import Navbar from "../components/Navbar";
import './Detail.css';
import '../styles/LinkText.css';
import FetchRSIData from "../components/FetchRSIData";


function Detail() {
    const location = useLocation();
    const initialStockCode = location.state?.stockCode || "";  // Home.js에서 전달된 초기 stockCode
    const initialStockName = location.state?.stockName || "";  // Home.js에서 전달된 초기 stockName
    const [stockData, setStockData] = useState([]);
    const [stockList, setStockList] = useState([]);
    const [rsiData, setRsiData] = useState([]);
    const [availableStocks, setAvailableStocks] = useState([]);
    const [selectedStock, setSelectedStock] = useState({ value: initialStockCode, label: initialStockName });
    const [filteredData, setFilteredData] = useState([]);
    const [activeTab, setActiveTab] = useState(null);
    const [inputValue, setInputValue] = useState("");

    const handleDataFetch = (data) => {
        setStockData(data);
    };

    const handleRsiDataFetch = (data) => {
        setRsiData(data);
    }

    const handleListFetch = (data) => {
        setStockList(data);
    };

    const handleTabClick = (tab) => {
        setActiveTab(tab);
    };

    useEffect(() => {
        if (stockData.length > 0 && stockList.length > 0) {
            const available = stockList.filter(stock =>
                stockData.some(data => data.stock_code === stock.code)
            ).map(stock => ({
                value: stock.code,
                label: `${stock.name}`
            }));
            setAvailableStocks(available);
        }
    }, [stockData, stockList]);

    useEffect(() => {
        if (stockData.length > 0 && selectedStock) {
            const filtered = stockData.filter(data => data.stock_code === selectedStock.value)
                .map(data => ({
                    date: data.date,
                    close: data.close,
                    high: data.high,
                    low: data.low,
                    open: data.open,
                    volume: data.volume
                }))
                .sort((b, a) => new Date(a.date) - new Date(b.date));

            console.log("Filtered Data:", filtered);
            setFilteredData(filtered);
        }
    }, [stockData, selectedStock]);

    const handleSelectChange = (selectedOption) => {
        setSelectedStock(selectedOption);
    };

    const customStyles = {
        container: (provided) => ({
            ...provided,
            width: '450px'
        })
    };

    return (
        <>
            <Navbar />

            <div className="detail-container">

                <div className="search-bar">
                    <Select
                        onBlurResetsInput={false}
                        inputValue={inputValue}
                        onInputChange={(value) => setInputValue(value)}
                        options={availableStocks}
                        onChange={handleSelectChange}
                        value={selectedStock}
                        placeholder="종목을 검색하세요"
                        styles={customStyles}
                    />
                </div>

                <div className="indicator-tabs">
                    <button className={activeTab === 'RSI' ? 'active-tab' : ''} onClick={() => handleTabClick('RSI')}>RSI</button>
                    <button className={activeTab === 'MACD' ? 'active-tab' : ''} onClick={() => handleTabClick('MACD')}>MACD</button>
                </div>

                <div className="detail-main-content">
                    <div className="detail-chart">
                        <FetchLatestStockData stockCode={selectedStock.value} onDataFetch={handleDataFetch} />
                        <FetchRSIData stockCode={selectedStock.value} onRSIFetch={handleRsiDataFetch} />
                        <FetchStockList onListFetch={handleListFetch} />
                        {selectedStock && (
                            <>
                                <h3 className="detail-chart-text">{selectedStock.label} 차트</h3>
                                <ChartOfClose stockCode={selectedStock.value} />

                                {activeTab === "RSI" && (
                                    <ChartOfRSI stockCode={selectedStock.value} rsiData={rsiData} />
                                )}
                            </>
                        )}
                    </div>

                    <div className="analysis-display">
                        <h3 className="analysis-display-main-text"> {selectedStock.label} 분석</h3>
                        <div className="textbox">
                            {activeTab === "RSI" && (
                                <RSIAnalysis1 stockCode={selectedStock.value} />
                            )}
                        </div>
                        <div className="textbox">
                            {activeTab === "RSI" && (
                                <RSIAnalysis2 stockCode={selectedStock.value} stockData={stockData} rsiData={rsiData} />
                            )}
                        </div>
                        <div className="textbox">
                            {activeTab === "RSI" && (
                                <RSIAnalysis3 rsiData={rsiData} />
                            )}
                        </div>
                        <div className="textbox">
                            {activeTab === "RSI" && (
                                <RSIAnalysis4 rsiData={rsiData} />
                            )}
                        </div>
                    </div>
                </div>

                <div className="detail-table">
                    <h3>{selectedStock ? selectedStock.label : "종목"} 종목 데이터</h3>
                    <table>
                        <thead>
                        <tr>
                            <th>일자</th>
                            <th>종가</th>
                            <th>고가</th>
                            <th>저가</th>
                            <th>시가</th>
                            <th>거래량</th>
                        </tr>
                        </thead>
                        <tbody>
                        {filteredData.map((data, index) => (
                            <tr key={index}>
                                <td>{data.date}</td>
                                <td>{data.close}</td>
                                <td>{data.high}</td>
                                <td>{data.low}</td>
                                <td>{data.open}</td>
                                <td>{data.volume}</td>
                            </tr>
                        ))}
                        </tbody>
                    </table>
                </div>
            </div>
        </>
    );
}

export default Detail;
